import React, { useState } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBBtn,
} from "mdb-react-ui-kit";
import axiosInstance from "../../axiosApi";

function TransactionForm() {

  const [eori_nr, setEoriNr] = useState("");
  const [importeur, setImporteur] = useState("");
  const [description, setDescription] = useState("");



  function handleSubmit(e) {
    e.preventDefault();
    const newTransaction = {
      eori_nr,
      importeur,
      description,
    }
    console.log(newTransaction)
    //firma hinzufügen 
/*     axiosInstance
      .post('/companies/', newCompany)
      .then(console.log())
      .catch(error => {
          this.setState({ errorMessage: error.message });
          console.error('There was an error!', error);
      }).then(
        axiosInstance
        .post('/partnership/', {"added_partner":eori_nr})
        .then(console.log())
        .catch(error => {
            this.setState({ errorMessage: error.message });
            console.error('There was an error!', error);
        })); */
      //firma als partner hinterlegen
  };

  return (
    <>
    <form onSubmit={handleSubmit}>
      <MDBContainer>
        <MDBInput
          className="my-3"
          name="eori_nr"
          label="EORI-Nr."
          type="number"
          onChange={e => setEoriNr(e.target.value)}

        />
        <MDBInput
          className="mb-3"
          name="rolle"
          label="Importeur?"
          type="text"
          onChange={e => setImporteur(e.target.value)}
        />
        <MDBInput
          className="mb-3"
          name="description"
          label="Beschreibung der Transaktion"
          type="text"
          onChange={e => setDescription(e.target.value)}
        />

        <MDBBtn type="submit" color="success">Hinzufügen</MDBBtn>

      </MDBContainer>
    </form>
    </>
  );
}

export default TransactionForm;
