import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import useAxiosPrivate from "../../hooks/useAxiosPrivate";
import {
  MDBContainer,
  MDBBtn,
  MDBRow,
  MDBCol,
  MDBSpinner,
  MDBBadge,
} from "mdb-react-ui-kit";
import Divider from "@mui/material/Divider";

import { Link } from "react-router-dom";
import { MDBBreadcrumb, MDBBreadcrumbItem } from "mdb-react-ui-kit";

import ZollDocumentsModal from "../../components/ZollDocuments/ZollDocumentsModal";
import ZollDocumentTransaction from "../../components/ZollDocuments/ZollDocumentTransaction";
import DocumentCard from "../../components/Documents/DocumentCard";

function ZollDocuments(props) {
  // Allgemeine Auftragsinformationen
  const [transactionData, setTransactionData] = useState([]);

  // Informationen der hochgeladenen Dokumente
  const [documentsData, setDocumentsData] = useState([]);

  const [allData, setAllData] = useState([]);


  const [isLoading, setIsLoading] = useState(true);

  const axiosInstance = useAxiosPrivate();

  // // Anzahl Dokumente
  // const [numberInvoiceDocumentes, setNumberInvoiceDocuments] = useState(0);
  // const [numberOtherDocumentes, setNumberOtherDocuments] = useState(0);

  // Speichern der Transaktions-ID aus URL
  const { transactionId } = useParams();

  useEffect(() => {
    axiosInstance.get(`declaration_auth/` + 1).then((res) => {
      console.log(res.data);
      setAllData(res.data)
      setDocumentsData(res.data.documents);
      setTransactionData(res.data.transaction);
      setIsLoading(false);
    }).catch((err) =>{
      alert("Keine Zollanmeldung liegt vor" + err)
    });
/*     console.log(transactionData);
    axiosInstance.get(`documents/?transaction=` + transactionId).then((res) => {
      //console.log(res.data);
      setDocumentsData(res.data);
      //console.log(documentsData);
    }); */
  }, []);

  // State für "Dokument hinzufügen" Modal
  const [addDocumentModal, setAddDocumentModal] = useState(false);
  // Funktion für Manipulation von Modal (als props weitergereicht)
  function toggleAddDocumentModal() {
    setAddDocumentModal(!addDocumentModal);
  }

  // Anzahl Rechnungs+Zolldokumente call(a) und sonstige Dokumente call(b)
  function numberInvoiceDocuments(alternative) {
    let invoiceAndCustoms = 0;
    let other = 0;
    for (let i = 0; i < documentsData.length; i++) {
      if (
        documentsData[i].type == "Rechnung" ||
        documentsData[i].type == "Zoll"
      ) {
        invoiceAndCustoms += 1;
      } else {
        other += 1;
      }
    }
    if (alternative == "a") {
      return invoiceAndCustoms;
    }
    if (alternative == "b") {
      return other;
    }
    return;
  }

  return (
    <>
      {isLoading ? (
        <MDBSpinner grow>
          <span className="visually-hidden">Lädt..</span>
        </MDBSpinner>
      ) : (
        <MDBContainer>
          <h3>Zollanmeldung Dokumente</h3>
          <ZollDocumentsModal
            basicModal={addDocumentModal}
            setBasicModal={setAddDocumentModal}
            toggleShow={toggleAddDocumentModal}
            transactionData={transactionData}
          />
          <MDBBreadcrumb>
            <MDBBreadcrumbItem>
              <Link to="/zoll/transactions">Aufträge</Link>
            </MDBBreadcrumbItem>
            <MDBBreadcrumbItem active>
              Auftrags-ID {transactionId}
            </MDBBreadcrumbItem>
          </MDBBreadcrumb>
          <ZollDocumentTransaction
            description={transactionData.description}
            dateAdded={transactionData.date_added}
            dateProcessed={transactionData.date_processed}
            status={transactionData.status}
            street={transactionData.partner1?.street}
            cityCode={transactionData.partner1?.city_code}
            countryCode={transactionData.partner1?.country_code}
            city={transactionData.partner1?.city}
            name={transactionData.partner1?.name}
            id={transactionData.id}
            blockchain_rid={transactionData.blockchain_id}
            numberDocuments={documentsData.length}
          />

          {/* Navleiste mit Anzahl der Dokumente */}
          <nav className="my-3 navbar navbar-expand-lg navbar-light bg-light">
            <MDBContainer>
              <div>
                <MDBBadge pill>{numberInvoiceDocuments("a")}</MDBBadge>{" "}
                <b>Rechnungs- und Zolldokumente</b>
                <MDBBadge pill className="ms-3">
                  {numberInvoiceDocuments("b")}
                </MDBBadge>{" "}
                <b>Sonstige Dokumente</b>
              </div>
              <MDBBtn
                outline
                className="float-end"
                color="success"
                onClick={toggleAddDocumentModal}
              >
                Dokument hinzufügen
              </MDBBtn>
            </MDBContainer>
          </nav>
          {/* Auflistung der Rechnungsdokumente */}

          <Divider textAlign="left" className="mb-4">
            <h5>Rechnungs- und Zolldokumente</h5>
          </Divider>
          <MDBRow className="row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
            {documentsData.map(
              (documentData) =>
                documentData.type == "Rechnung" && (
                  <React.Fragment>
                    <MDBCol>
                      <DocumentCard
                        key={documentData.id}
                        id={documentData.id}
                        author_company={
                          documentData.owner.employee.company.name
                        }
                        author={documentData.owner.username}
                        type={documentData.type}
                        description={documentData.description}
                        documentDate={documentData.issue_date}
                        uploadDate={documentData.upload_date}
                        file={documentData.file}
                        confirmed={documentData.confirmed}
                      />
                    </MDBCol>
                  </React.Fragment>
                )
            )}
          </MDBRow>

          {/* Auflistung der sontigen Dokumente */}

          <Divider textAlign="left" className="mt-5 mb-4">
            <h5>Sonstige Dokumente</h5>
          </Divider>
          <MDBRow className="row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
            {documentsData.map(
              (documentData) =>
                documentData.type !== "Rechnung" && (
                  <React.Fragment>
                    <MDBCol>
                      <DocumentCard
                        key={documentData.id}
                        id={documentData.id}
                        author={documentData.author}
                        type={documentData.type}
                        description={documentData.description}
                        documentDate={documentData.issue_date}
                        uploadDate={documentData.upload_date}
                        file={documentData.file}
                        confirmed={documentData.confirmed}
                      />
                    </MDBCol>
                  </React.Fragment>
                )
            )}
          </MDBRow>
        </MDBContainer>
      )}
    </>
  );
}

export default ZollDocuments;
