import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import useAxiosPrivate from "../hooks/useAxiosPrivate";
import PreviewInvoice from "../components/Documents/PreviewInvoice";
import PreviewOther from "../components/Documents/PreviewOther";
import { MDBSpinner } from "mdb-react-ui-kit";

function DocumentDetail () {

    //URL Parameter abfragen
    const { documentID } = useParams()
    const axiosInstance = useAxiosPrivate()
    const [documentData, setDocumentData] = useState()
    const [invoiceData, setInvoiceData] = useState()
    const [transactionData, setTransactionData] = useState()
    const [isLoading, setIsLoading] = useState(true)

    const [isInvoice, setIsInvoice] = useState(false)



    useEffect(() => {
        console.log(documentData)
    }, [documentData])

    useEffect(() => {
        axiosInstance.get(`/documents/${documentID}/`)
        .then((res) => {
            setDocumentData(res.data)
            axiosInstance.get(`/documents/${documentID}/`)
            .then((res) => {
                setTransactionData(res.data)
                if (res.data.type=="Rechnung") {
                    axiosInstance.get(`/invoice/${documentID}/`)
                        .then((res) => {
                            setInvoiceData(res.data)
                            if (res.data.type=="Rechnung") {
                                setIsInvoice(true)
                                setIsLoading(false)
                            }
                        })

                } else {
                    setIsLoading(false)
                }
            })

        })
    }, [])

    useEffect(() => {

    },[])

    return (<>
        {isLoading ? (        

            <MDBSpinner grow>
                <span className="visually-hidden">LÃ¤dt..</span>
            </MDBSpinner>) 
        : (
            isInvoice ? (
                <PreviewInvoice 
                        invoiceData = {invoiceData}
                        transactionData = {transactionData}
                ></PreviewInvoice>    
            ): (
                <PreviewOther
                    documentData = {documentData}
                    transactionData = {transactionData}
                ></PreviewOther>
            )
            )}
    </>


    )
}

export default DocumentDetail;
