import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import useAxiosPrivate from "../hooks/useAxiosPrivate";
import { MDBContainer, MDBBtn, MDBRow, MDBCol } from "mdb-react-ui-kit";
import Divider from "@mui/material/Divider";
import Chip from "@mui/material/Chip";

import DocumentsModal from "../components/Documents/DocumentsModal";
import DocumentTransaction from "../components/Documents/DocumentTransaction";
import DocumentCard from "../components/Documents/DocumentCard";
import DocumentBreadcrump from "../components/Documents/DocumentBreadcrumb";

function DocumentsNew(props) {
  // Allgemeine Auftragsinformationen
  const [transactionData, setTransactionData] = useState([]);

  // Informationen der hochgeladenen Dokumente
  const [documentsData, setDocumentsData] = useState([]);

  const axiosInstance = useAxiosPrivate();

  // Anzahl Dokumente
  const [numberInvoiceDocumentes, setNumberInvoiceDocuments] = useState(0);
  const [numberOtherDocumentes, setNumberOtherDocuments] = useState(0);

  // State für "Dokument hinzufügen" Modal
  const [addDocumentModal, setAddDocumentModal] = useState(false);
  // Funktion für Manipulation von Modal (als props weitergereicht)
  function toggleAddDocumentModal() {
    setAddDocumentModal(!addDocumentModal);
  }

  // Speichern der Transaktions-ID aus URL
  const { transactionId } = useParams();

  useEffect(() => {
    axiosInstance.get(`transactions/` + transactionId).then((res) => {
      console.log(res.data);
      setTransactionData(res.data);
    });
    console.log(transactionData);
    axiosInstance.get(`documents/?transaction=` + transactionId).then((res) => {
      console.log(res.data);
      setDocumentsData(res.data);
      console.log(documentsData);
    });
    //leere Array am Ende von UseEffect zum einmaligen Rendern - https://www.digitalocean.com/community/tutorials/how-to-call-web-apis-with-the-useeffect-hook-in-react
  }, []);

  return (
    <MDBContainer>
      <h3>
        Dokumente (neu)
        <MDBBtn
          outline
          className="float-end"
          color="success"
          onClick={toggleAddDocumentModal}
        >
          Dokument hinzufügen
        </MDBBtn>
      </h3>
      <DocumentsModal
        basicModal={addDocumentModal}
        setBasicModal={setAddDocumentModal}
        toggleShow={toggleAddDocumentModal}
      />
      <DocumentBreadcrump key={transactionId} transactionId={transactionId} />
      <DocumentTransaction
        description={transactionData.description}
        dateAdded={transactionData.date_added}
        dateProcessed={transactionData.date_processed}

        /* 
        MACHT PROBLEME - Genauer untersuchen!
        name={transactionData.partnership.partner.name}
        street={transactionData.partnership.partner.street}
        cityCode={transactionData.partnership.partner.city_code}
        city={transactionData.partnership.partner.city}
        countryCode={transactionData.partnership.partner.country_code} */
      />
      <br />
      <br />

      {/* Auflistung der Rechnungsdokumente */}

      <Divider textAlign="left" className="mb-4">
        <h5>Rechnungsdokumente</h5>
      </Divider>
      <MDBRow className="row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
        {documentsData.map(
          (documentData) =>
            documentData.type == "Rechnung" && (
              <React.Fragment>
                <MDBCol>
                  <DocumentCard
                    key={documentData.documentID}
                    id={documentData.documentID}
                    author={documentData.author}
                    type={documentData.type}
                    description={documentData.description}
                    documentDate={documentData.issue_date}
                    uploadDate={documentData.uploadDate}
                    file={documentData.file}
                  />
                </MDBCol>
              </React.Fragment>
            )
        )}
      </MDBRow>

      {/* Auflistung der sontigen Dokumente */}

      <Divider textAlign="left" className="mt-5 mb-4">
        <h5>Sonstige Dokumente</h5>
      </Divider>
      <MDBRow className="row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
        {documentsData.map(
          (documentData) =>
            documentData.type !== "Rechnung" && (
              <React.Fragment>
                <MDBCol>
                  <DocumentCard
                    key={documentData.documentID}
                    id={documentData.documentID}
                    author={documentData.author}
                    type={documentData.type}
                    description={documentData.description}
                    documentDate={documentData.issue_date}
                    uploadDate={documentData.uploadDate}
                    file={documentData.file}
                  />
                </MDBCol>
              </React.Fragment>
            )
        )}
      </MDBRow>
    </MDBContainer>
  );
}

export default DocumentsNew;
