import React, { useState } from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBCardHeader,
  MDBBtn,
  MDBRow,
  MDBCol,
  MDBIcon,
  MDBBreadcrumb,
  MDBBreadcrumbItem,
} from "mdb-react-ui-kit";
import { Link } from "react-router-dom";

// Importierte PDF
import LieferscheinMuster from "../../LieferscheinMuster.pdf";
import JSCheatSheet from "../../Javascript-Cheat-Sheet.pdf";

// PDF-Reader
import { Document, Page } from "react-pdf";
import { pdfjs } from "react-pdf";
pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/legacy/build/pdf.worker.min.js`;

// pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

const url =
  "https://cors-anywhere.herokuapp.com/http://www.pdf995.com/samples/pdf.pdf";

export default function PreviewOther(props) {
  // -- DATENFELDER --

  // Auftrag Metadaten
  // const [documentMetadata, setDocumentMetadata] = React.useState({
  //   documentId: props.documentData.id,
  //   transactionId: props.documentData.transaction,
  //   documentType: props.documentData.type,
  //   dateAdded: props.documentData.upload_date,
  //   //author: "LG Electronics",
  //   description: props.documentData.description,
  // });

  const [documentMetadata, setDocumentMetadata] = React.useState({
    documentId: 18,
    transactionId: 43,
    documentType: "Lieferschein",
    dateAdded: "17.06.2022",
    // author: "LG Electronics",
    description: "PDF Lieferschein",
    url: "https://res.cloudinary.com/liebichmedien/image/upload/v1/media/docs/Javascript-Cheat-Sheet_vlj790",
  });

  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  function changePage(offSet) {
    setPageNumber((prevPageNumber) => prevPageNumber + offSet);
  }

  function changePageBack() {
    changePage(-1);
  }

  function changePageNext() {
    changePage(+1);
  }

  return (
    <>
      <h3>Dokument: Sonstige</h3>

      <MDBBreadcrumb>
        <MDBBreadcrumbItem>
          <Link to="/transactions">Aufträge</Link>
        </MDBBreadcrumbItem>
        <MDBBreadcrumbItem>
          <Link to={"/transactions/" + documentMetadata.transactionId}>
            Auftrags-ID 18
          </Link>
        </MDBBreadcrumbItem>
        <MDBBreadcrumbItem active>
          Dokumenten-ID {documentMetadata.documentId}
        </MDBBreadcrumbItem>
      </MDBBreadcrumb>
      <MDBCard style={{ minHeight: "1000px" }}>
        <MDBCardHeader>
          <h5>
            {documentMetadata.documentType} für Auftrags-ID{" "}
            {documentMetadata.transactionId}
          </h5>
        </MDBCardHeader>
        <MDBCardBody>
          <MDBRow>
            <MDBCol className="col-md-8">
              <div
                className="d-flex justify-content-center"
                style={{ minHeight: "800px" }}
              >
                <Document
                  // PDF muss Objekt sein
                  file={{
                    url: documentMetadata.url,
                  }}
                  onLoadSuccess={onDocumentLoadSuccess}
                >
                  <Page pageNumber={pageNumber} />
                </Document>
              </div>
              <div className="d-flex justify-content-center">
                <p>
                  Seite {pageNumber} von {numPages}
                </p>
              </div>
              {pageNumber > 1 ? (
                <MDBBtn
                  outline
                  className="float-start me-3"
                  onClick={changePageBack}
                >
                  <MDBIcon className="me-1" size="lg" fas icon="chevron-left" />
                  Letzte Seite
                </MDBBtn>
              ) : (
                <MDBBtn
                  outline
                  className="float-start me-3"
                  disabled
                  onClick={changePageBack}
                >
                  <MDBIcon className="me-1" size="lg" fas icon="chevron-left" />
                  Letzte Seite
                </MDBBtn>
              )}
              {pageNumber < numPages ? (
                <MDBBtn outline className="float-end" onClick={changePageNext}>
                  Nächste Seite
                  <MDBIcon
                    className="ms-1"
                    size="lg"
                    fas
                    icon="chevron-right"
                  />
                </MDBBtn>
              ) : (
                <MDBBtn
                  outline
                  className="float-end"
                  disabled
                  onClick={changePageNext}
                >
                  Nächste Seite
                  <MDBIcon
                    className="ms-1"
                    size="lg"
                    fas
                    icon="chevron-right"
                  />
                </MDBBtn>
              )}
            </MDBCol>
            <MDBCol
              className="col-md-4"
              style={{ border: "3px solid #cccccc", height: 500 }}
            >
              <p className="mt-4 ms-3">
                <b>Auftrags-ID: </b>
                {documentMetadata.transactionId}
              </p>
              <p className="ms-3">
                <b>Dokumenten-ID: </b>
                {documentMetadata.documentId}
              </p>
              <p className="ms-3">
                <b>Dokumententyp: </b>
                {documentMetadata.documentType}
              </p>
              <p className="ms-3">
                <b>Hochgeladen am: </b>
                {documentMetadata.dateAdded}
              </p>
              {/*               <p className="ms-3">
                <b>Hochgeladen von: </b>
                {documentMetadata.author}
              </p> */}

              <p className="ms-3 mb-5">
                <b>Beschreibung: </b>
                {documentMetadata.description == "" ? (
                  <i>Es liegt keine Beschreibung vor.</i>
                ) : (
                  documentMetadata.description
                )}
              </p>

              <MDBBtn download href={props.fileLink} className="ms-3 mt-2">
                <MDBIcon fas icon="download" size="lg" className="me-1" />
                Download
              </MDBBtn>
            </MDBCol>
          </MDBRow>
        </MDBCardBody>
      </MDBCard>
    </>
  );
}
