import React from 'react'
import { useRef, useState, useEffect } from 'react';

import {
  MDBInput,
  MDBCol,
  MDBRow,
  MDBContainer,
  MDBBtn,
} from "mdb-react-ui-kit";

const LoginNew = () => {

/*   const userRef = useRef();
  const errRef = useRef(); */

  const [user, setUser] = useState(''); 
  const [pwd, setPwd] = useState(''); 
  const [errMsg, setErrMsg] = useState(''); 
  const [sucess, setSucess] = useState(false);

/*   useEffect(() => {
    userRef.current.focus();
  }, []) */


  useEffect(() => {
    setErrMsg('');
  }, [user, pwd])


  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(user,pwd);
    setUser('');
    setPwd('');
    setSucess(true);
  }


 /*    axiosInstance
      .post("api/token/", {
        username: this.state.username,
        password: this.state.password,
      })
      .then((response) => {
        axiosInstance.defaults.headers["Authorization"] =
          "JWT " + response.data.access;
        localStorage.setItem("access_token", response.data.access);
        localStorage.setItem("refresh_token", response.data.refresh);
        //speichern von Nutzernamen und Firma im lokalen Speicher
        localStorage.setItem("username", response.data.user);
        localStorage.setItem("company", response.data.company);
        alert(
          "Login als " +
            response.data.user +
            " von der Firma " +
            response.data.company
        );
        window.location.reload(true);
        return response.data;
        //Error Handling
      })
      .catch(function (error) {
        if (error.response) {
          // Macht noch Probleme:
          this.setState({ wrongLogin: true });
          // Request made and server responded
          alert("Passwort oder Nutzername falsch");
          console.log(error.response.data);
          console.log(error.response.status);
          console.log(error.response.headers);
        } else if (error.request) {
          // The request was made but no response was received
          alert("Anfrage gemacht - Server antwortet nicht");

          console.log(error.request);
        }
      }); */


  return (
    <>
      {sucess ? (
          <section>
              <h1> Du bist eingeloggt</h1>
              <br />
              <p>
                <a href="#">Zur Startseite</a>
              </p>
          </section>

      ):(
    <section> 
      <div>
        <p className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
        <MDBContainer>
            <MDBRow className="d-flex justify-content-center">
              <MDBCol className="col-md-4">
              <form onSubmit={handleSubmit}>
                  <MDBInput
                    className="mb-4"
                    type="username"
                    id="username"
                    label="Email address"
                    name="username"

                    onChange={(e) => setUser(e.target.value)}
                  />

                  <MDBInput
                    className="mb-4"
                    type="password"
                    id="password"
                    label="Password"
                    name="password"
                    onChange={(e) => setPwd(e.target.value)}
                  />

                  <MDBBtn type="submit" value="Submit">
                    Einloggen
                  </MDBBtn>
                </form>
                <br />
              </MDBCol>
            </MDBRow>
          </MDBContainer> 
      </div>
    </section>
      )}
    </>      
  )
  }

export default LoginNew
