import React, { useState } from "react";
import { MDBContainer, MDBInput, MDBRow, MDBCol } from "mdb-react-ui-kit";

import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";

import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select, { SelectChangeEvent } from "@mui/material/Select";
import FormHelperText from "@mui/material/FormHelperText";

function Rechnungsdaten(props) {
  return (
    <>
      <h5 className="mb-4">Rechnungsdaten</h5>

      <MDBRow>
        <MDBCol>
        <Autocomplete
                  className="mb-3"
                  disablePortal
                  id="combo-box-customOffices"
                  options={props.customOffices}
                  //choose name as label for ui
                  getOptionLabel={(option) => option.name}
                  //when changed, save choosen Partner
                  onChange={(event, value) => {
                    console.log(value.id)
                    props.setFieldValue(prev => {
                      return {
                          ...prev,
                          bearbeitendeDienststelle: value.id,
                      }
                    });
                    props.setPubkeyCO(value.publickey)

                  }}
                  sx={{ width: 320 }}
                  renderInput={(params) => (
                    <TextField {...params} label="bearbeitende Dienststelle" />
                  )}
                />

        </MDBCol>
        <MDBCol>
          <TextField
            className="mb-3"
            sx={{ minWidth: 250, maxWidth: 250 }}
            type="date"
            id="datumRechnung"
            name="datumRechnung"
            label="Rechnungsdatum*"
            variant="outlined"
            disabled
            value={props.fieldValue.datumRechnung}
            error={
              props.inputMissingError.datumRechnung ||
              props.inputWrongError.datumRechnung
            }
            helperText={
              props.inputMissingError.datumRechnung ? "Pflichtfeld" : null
            }
            onChange={(event) => props.handleFieldChange(event)}
          />
        </MDBCol>
      </MDBRow>
      <MDBRow>
        <MDBCol>
          <TextField
            sx={{ minWidth: 250, maxWidth: 250 }}
            className="mb-3 me-3"
            id="gesamtbetrag"
            name="gesamtbetrag"
            label="Gesamtbetrag*"
            variant="outlined"
            disabled
            value={props.fieldValue.gesamtbetrag}
            error={
              props.inputMissingError.gesamtbetrag ||
              props.inputWrongError.gesamtbetrag
            }
            helperText={
              props.inputMissingError.eoriVersender
                ? "Pflichtfeld"
                : props.inputWrongError.eoriVersender
                ? "Zahl eingeben"
                : null
            }
            onChange={(event) => props.handleNumberFieldChange(event)}
          />

          <TextField
            className="mb-3"
            id="waehrung"
            name="waehrung"
            label="Währung*"
            variant="outlined"
            disabled
            value={props.fieldValue.waehrung}
            error={
              props.inputMissingError.waehrung || props.inputWrongError.waehrung
            }
            helperText={props.inputMissingError.waehrung ? "Pflichtfeld" : null}
            onChange={(event) => props.handleFieldChange(event)}
          />
        </MDBCol>
      </MDBRow>
      <MDBRow>
        <MDBCol>
          <TextField
            className="mb-3 me-3"
            sx={{ minWidth: 250, maxWidth: 250 }}
            id="lieferkosten"
            name="lieferkosten"
            label="Lieferkosten*"
            variant="outlined"
            error={
              props.inputMissingError.lieferkosten ||
              props.inputWrongError.lieferkosten
            }
            helperText={
              props.inputMissingError.lieferkosten ? "Pflichtfeld" : null
            }
            onChange={(event) => props.handleNumberFieldChange(event)}
          />
        </MDBCol>
      </MDBRow>
      <hr className="my-4" />
      {/* Rechnungsposten müssen gemapt werden: */}
      <MDBRow className="mb-3">
        <MDBCol>
          <b>Rechnungsposten 1:</b>
        </MDBCol>
      </MDBRow>
      <MDBRow>
        <MDBCol>
          <TextField
            sx={{ minWidth: 400, maxWidth: 400 }}
            className="mb-3"
            value={props.rechnungsposten.rechnungspostenProdukt}
            id="rechnungspostenProdukt"
            name="rechnungspostenProdukt"
            label="Produkt*"
            variant="outlined"
          />
        </MDBCol>
      </MDBRow>
      <MDBRow>
        <MDBCol>
          <TextField
            sx={{ minWidth: 200, maxWidth: 200 }}
            className="mb-3 me-3"
            value={props.rechnungsposten[0].rechnungspostenAnzahl}
            id="rechnungspostenAnzahl"
            name="rechnungspostenAnzahl"
            label="Anzahl*"
            variant="outlined"
          />
          <TextField
            className="mb-3 me-3"
            sx={{ minWidth: 200, maxWidth: 200 }}
            value={props.rechnungsposten[0].rechnungspostenEinheit}
            id="rechnungspostenEinheit"
            name="rechnungspostenEinheit"
            label="Einheit*"
            variant="outlined"
          />
          <TextField
            className="mb-3 me-3"
            sx={{ minWidth: 200, maxWidth: 200 }}
            value={props.rechnungsposten[0].rechnungspostenPreisEinheit}
            id="rechnungspostenPreisEinheit"
            name="rechnungspostenPreisEinheit"
            label="Preis pro Einheit*"
            variant="outlined"
          />
        </MDBCol>
      </MDBRow>
      <hr className="my-4" />

      {/* Abgaben Berechnung: */}
      <MDBRow>
        <p className="ms-4">
          <b>Zollwert</b> (Geamtbetrag + Lieferkosten) ={" "}
          {(
            Number(props.fieldValue.gesamtbetrag) +
            Number(props.fieldValue.lieferkosten)
          ).toFixed(2)}{" "}
          {props.fieldValue.waehrung}
        </p>
        <p className="ms-4">
          <b>Zollbetrag</b> (Pauschaler Zollsatz 4 %) ={" "}
          {(
            (Number(props.fieldValue.gesamtbetrag) +
              Number(props.fieldValue.lieferkosten)) *
            Number(0.04)
          ).toFixed(2)}{" "}
          {props.fieldValue.waehrung}
        </p>
        <p className="ms-4">
          <b>Einfuhrumsatzsteuer(EUSt)-Wert</b> (Zollwert + Zollbetrag) ={" "}
          {(
            (Number(props.fieldValue.gesamtbetrag) +
              Number(props.fieldValue.lieferkosten)) *
            Number(1.04)
          ).toFixed(2)}{" "}
          {props.fieldValue.waehrung}
        </p>
        <p className="ms-4">
          <b>EUSt-Betrag</b> (EUSt-Wert * EUSt-Satz 19 %) ={" "}
          {(
            (Number(props.fieldValue.gesamtbetrag) +
              Number(props.fieldValue.lieferkosten)) *
            Number(1.04) *
            Number(0.19)
          ).toFixed(2)}{" "}
          {props.fieldValue.waehrung}
        </p>
        <p className="ms-4">
          <b>Prognostizierte Gesamtabgabe</b> (Zollbetrag + EUSt-Betrag) ={" "}
          <u>
            {(
              (Number(props.fieldValue.gesamtbetrag) +
                Number(props.fieldValue.lieferkosten)) *
                Number(0.04) +
              (Number(props.fieldValue.gesamtbetrag) +
                Number(props.fieldValue.lieferkosten)) *
                Number(1.04) *
                Number(0.19)
            ).toFixed(2)}{" "}
            {props.fieldValue.waehrung}
          </u>
        </p>
      </MDBRow>
    </>
  );
}

export default Rechnungsdaten;
