import React, { useEffect, useState } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBBtn,
  MDBSwitch,
} from "mdb-react-ui-kit";
import axiosInstance from "../../axiosApi";
import Autocomplete from '@mui/material/Autocomplete';
import TextField from '@mui/material/TextField';
import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';


function TransactionForm() {

  const [eori_nr, setEoriNr] = useState("");
  const [check, setCheck] = useState(false);  
  const [description, setDescription] = useState("");
  const [partner, setPartner] = useState("")
  const [partners, setPartners] = useState([])
  const top100Films = [
    { label: 'The Godfather', id: 1 },
    { label: 'Pulp Fiction', id: 2 },
  ];

/*   function onTagsChange (event, values) {
    setPartner({
      tags: values
    }, () => {
      // This will output an array of objects
      // given by Autocompelte options property.
      console.log(partner);
    });
  } */

  useEffect(() => {
    axiosInstance.get('partnership/').then((res) => {
      console.log(res.data);
      setPartners(res.data);
    });
  }, []);
  function handleSubmit(e) {
    e.preventDefault();
    const newTransaction = {
      description,
      partner,
      check
    }
    console.log(newTransaction)
    //firma hinzufügen 
/*     axiosInstance
      .post('/companies/', newCompany)
      .then(console.log())
      .catch(error => {
          this.setState({ errorMessage: error.message });
          console.error('There was an error!', error);
      }).then(
        axiosInstance
        .post('/partnership/', {"added_partner":eori_nr})
        .then(console.log())
        .catch(error => {
            this.setState({ errorMessage: error.message });
            console.error('There was an error!', error);
        })); */
      //firma als partner hinterlegen
  };

  return (
    <>
    <form onSubmit={handleSubmit}>
      <MDBContainer>
        <Autocomplete
            disablePortal
            id="combo-box-partner"
            options={partners}
            //choose name as label for ui
            getOptionLabel={option => option.partner.name}
            //when changed, save choosen Partner
            onChange={(event, value) => setPartner(value.id)}
            sx={{ width: 300 }}
            renderInput={(params) => <TextField {...params} label="Partner" />}
        />
        <MDBRow>
          <MDBInput
            className="mb-3"
            name="description"
            label="Beschreibung der Transaktion"
            type="text"
            onChange={e => setDescription(e.target.value)}
          />
        </MDBRow>
        <FormControlLabel control={
          <Checkbox
            onClick={() => setCheck(!check)}
          />

          } label="Ist ihre Firma der Importeur?" />
        <MDBRow>
          <MDBBtn type="submit" color="success">Hinzufügen</MDBBtn>
        </MDBRow>      

      </MDBContainer>
    </form>
    </>
  );
}

export default TransactionForm;
