import { React, useState, useEffect } from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardHeader,
  MDBCardFooter,
  MDBBtn,
} from "mdb-react-ui-kit";
import useAxiosPrivate from "../../hooks/useAxiosPrivate";
import CardModal from "./CardModal";
//Hook für Contextabruf 
import useAuth from "../../hooks/useAuth";
import { confirmRechnung } from "../../Web3Client";

function DocumentCard(props) {

  const axiosInstance = useAxiosPrivate();
  //Context mit Nutzerdaten abrufen 
  const { auth } = useAuth();
  const [datei, setDatei] = useState([]);
  let fileLink = null;
  const [ShowConfirmation, setShowConfirmation] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  const author_company = props.author_company;

  useEffect(() => {
    //Abruf des Link zur Datei
    console.log(auth.pubkey)
    axiosInstance.get("file/3").then((res) => {
      setDatei(res.data);
      fileLink = res.data.file;
/*     console.log(author_company + auth.company)
    console.log(props.confirmed) */
    setIsLoading(true)
    if (props.confirmed == true) {
      setShowConfirmation(false);
      console.log("bereits bestätigt, confirm auf True")
    } else if (author_company == auth.company) {
        //console.log("Firmen sind ungleich- Confirm auf False")
    } else {
        setShowConfirmation(true);
        //console.log("Confirm auf True")
    }
    },[]);


  }, [ShowConfirmation]);

  useEffect(() => {



  }, []);

  const confirmDocument = (e) => {
    //blockchain Transaktion
    confirmRechnung(0)
      .then((tx) => {
        console.log("Rechnung mit der RechnungID " + tx + " wurde bestätigt");
      })
      .catch((err) => {
        console.log("Rechnungsbestätigung fehlgeschlagen. Fehlermeldung: " + err);
    });
    axiosInstance.patch(`invoice/${props.id}/`, {
      confirmed: true, 
    })
    //Hier Blockchain Transaktion einfügen
    setShowConfirmation(false); 
  }

  // State für "Dokument anzeigen" Modal
  const [showDocumentModal, setShowDocumentModal] = useState(false);
  // Funktion für Manipulation von Modal (als props weitergereicht)
  function toggleShowDocumentModal() {
    setShowDocumentModal(!showDocumentModal);
  }

  return (
    <>
      <MDBCard className="h-100" style={{ maxWidth: "22rem" }}>
        <MDBCardHeader>Hochgeladen von: {props.author} - {props.author_company}</MDBCardHeader>
        <MDBCardBody>
          <MDBCardTitle>
            {props.type + " " + "(" + props.documentDate + ")"}
          </MDBCardTitle>     
          <MDBCardText>{props.description}
            <br/>
            {props.confirmed ? <span> Bestätigt</span>: <span> warten auf bestätigung</span>}

          </MDBCardText>
          <MDBBtn outline onClick={toggleShowDocumentModal}>
            Anzeigen
          </MDBBtn>
      {ShowConfirmation ? <MDBBtn outline onClick={confirmDocument}>
            Freigeben
          </MDBBtn>
          : <br/>
      }

          <MDBBtn outline onClick={toggleShowDocumentModal}>
            Löschen
          </MDBBtn>
        </MDBCardBody>
        <MDBCardFooter className="text-muted">
          Hochgeladen am: Test: {props.uploadDate}
        </MDBCardFooter>
      </MDBCard>
      <CardModal
        basicModal={showDocumentModal}
        setBasicModal={setShowDocumentModal}
        toggleShow={toggleShowDocumentModal}
        title={props.title}
        description={props.description}
      />
    </>
  );
}

export default DocumentCard;
