import { React, useState, useEffect } from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardHeader,
  MDBCardFooter,
  MDBBtn,
} from "mdb-react-ui-kit";
import Toast from "react-bootstrap/Toast";

export default function NotificationToast(props) {
    var timestamp = new Date(props.alert.timestamp)
    const [notificationText, setNotificationText] = useState("")

    useEffect(() =>{
        if (props.actor_content_type=="11") {
            const text= `Eine neue Transaktion mit der ID ${props.actor.actor_object_id}wurde angelegt`
            setNotificationText(text)
        } else {
            const text = "sonstige Benachrichtigung"
            setNotificationText(text)

        }
    }, [alert])


    return (
    <Toast
        className="mb-2"
        bg="light"
        style={{ width: "100%" }}
      >
        <Toast.Header>
          <strong className="me-auto">Auftrags-ID {props.alert.actor_object_id}</strong>
          <small>{timestamp.toLocaleString()}</small>
        </Toast.Header>
        <Toast.Body>
          {notificationText}
        </Toast.Body>
      </Toast>



    )
}
