import React from "react";
import { Link } from "react-router-dom";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardFooter,
  MDBCol,
  MDBRow,
} from "mdb-react-ui-kit";

function DeclarationCard(props) {
  return (
    <>
      <MDBCard>
        <MDBCardBody>
          <MDBRow>
            <MDBCol>
              <b>Art der Anmeldung</b>: {props.anmeldeArt}
            </MDBCol>
            <MDBCol>
              <b>Bearbeitende Dienststelle</b>: {props.bearbeitendeDienststelle}
            </MDBCol>
          </MDBRow>
          <MDBRow>
            <MDBCol>
              <b>Gesamtbetrag</b>: {props.gesamtbetrag}
            </MDBCol>
            <MDBCol>
              <b>Lieferkosten</b>: {props.lieferkosten}
            </MDBCol>
          </MDBRow>
          <MDBRow>
            <MDBCol>
              <b>Zollbetrag </b>(Pauschaler 4 % Zollsatz):{ (props.gesamtbetrag + props.lieferkosten) * 0.04}
            </MDBCol>
          </MDBRow>
          <MDBRow>
            <MDBCol>
              <b>EUSt-Betrag </b>(EUSt-Satz 19 %): { props.gesamtbetrag + props.lieferkosten * 0.19}
            </MDBCol>
          </MDBRow>
          <MDBRow>
            <MDBCol>
              <b>Prognostizierte Gesamtabgabe</b>: { props.gesamtbetrag + props.lieferkosten * 0.25}
            </MDBCol>
            <MDBCol>
              <MDBBtn outline className="float-end" color="primary">
                Anzeigen
              </MDBBtn>
            </MDBCol>
          </MDBRow>
        </MDBCardBody>
        <MDBCardFooter>Erstellt am ... von ...</MDBCardFooter>
      </MDBCard>
    </>
  );
}

export default DeclarationCard;
