import React, { useState, useEffect } from "react";
import useAxiosPrivate from "../hooks/useAxiosPrivate";

// Unterformulare
import AllgemeineAngaben from "../components/Declaration/AllgemeineAngaben";
import Adressdaten from "../components/Declaration/Adressdaten";
import Lieferdaten from "../components/Declaration/Lieferdaten";
import Rechnungsdaten from "../components/Declaration/Rechnungsdaten";

import { MDBContainer } from "mdb-react-ui-kit";

// Stepper
import Box from "@mui/material/Box";
import Stepper from "@mui/material/Stepper";
import Step from "@mui/material/Step";
import StepLabel from "@mui/material/StepLabel";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";

function Declaration() {
  // Stepper-Logik
  const steps = [
    "Allgemeine Angaben",
    "Adressdaten",
    "Lieferdaten",
    "Rechnungsdaten",
  ];

  const [activeStep, setActiveStep] = React.useState(0);
  const [skipped, setSkipped] = React.useState(new Set());

  // (Kein Step ist optional)
  const isStepOptional = (step) => {
    return;
  };


  //Abfrage der voreingestellten Daten
  const [transactionData, setTransactionData] = useState([]);

  const [isLoading, setIsLoading] = useState(true);

  const axiosInstance = useAxiosPrivate();

  const dataUpdate = async() => {
    const transactionId = 2; 
    const response = await axiosInstance.get(`transactions/` + transactionId);
    setTransactionData(response);
    return transactionData
  }

  useEffect(() => {

    const response =  dataUpdate();
    console.log(response)
    setFieldValue(prev => {
      return {
          ...prev,
          auftragsnummer: response.data.id,
      }
    });

  }, []);



  const isStepSkipped = (step) => {
    return skipped.has(step);
  };

  const handleNext = () => {
    let newSkipped = skipped;
    if (isStepSkipped(activeStep)) {
      newSkipped = new Set(newSkipped.values());
      newSkipped.delete(activeStep);
    }

    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    setSkipped(newSkipped);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleSkip = () => {
    if (!isStepOptional(activeStep)) {
      // You probably want to guard against something like this,
      // it should never occur unless someone's actively trying to break something.
      throw new Error("You can't skip a step that isn't optional.");
    }

    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    setSkipped((prevSkipped) => {
      const newSkipped = new Set(prevSkipped.values());
      newSkipped.add(activeStep);
      return newSkipped;
    });
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  const handleSubmit = () => {
    console.log(fieldValue);
    return;
  };



  // -- DATENFELDER --

  // State des Inhalts der Felder
  const [fieldValue, setFieldValue] = React.useState({
    // Allgemeine Angaben
    auftragsnummer:"",
    bearbeitendeDienststelle: "",
    anmeldungArt: "",
    geschaeftArt: "",
    zahlungArt: "",
    // Adressdaten Versender
    eoriVersender: "",
    nameVersender: "",
    vornameVersender: "",
    firmaVersender: "",
    strasseVersender: "",
    plzVersender: "",
    ortVersender: "",
    staatVersender: "",
    telefonVersender: "",
    emailVersender: "",
    // Adressdaten Anmelder
    eoriAnmelder: "",
    nameAnmelder: "",
    vornameAnmelder: "",
    firmaAnmelder: "",
    strasseAnmelder: "",
    plzAnmelder: "",
    ortAnmelder: "",
    staatAnmelder: "",
    telefonAnmelder: "",
    emailAnmelder: "",
    // Lieferdaten
    ausfuhrland: "",
    bestimmungsland: "",
    bestimmungsbundesland: "",
    befoerderungsmittel: "",
    lieferbedingung: "",
    lieferort: "",
    warenort: "",
    lieferkosten: "",
    //Rechnungsdaten
    datumRechnung: "",
    gesamtbetrag: "",
    waehrung: "",
  });

  const [rechnungsposten, setRechnungsposten] = React.useState([
    {
      rechnungspostenProdukt: "",
      rechnungspostenAnzahl: "",
      rechnungspostenEinheit: "",
      rechnungspostenPreisEinheit: "",
    },
  ]);

  // State über fehlende Eingaben
  const [inputMissingError, setInputMissingError] = React.useState({
    // Allgemeine Angaben
    auftragsnummer: false,
    bearbeitendeDienststelle: false,
    anmeldungArt: false,
    geschaeftArt: false,
    zahlungArt: false,
    // Adressdaten Versender
    eoriVersender: false,
    nameVersender: false,
    vornameVersender: false,
    firmaVersender: false,
    strasseVersender: false,
    plzVersender: false,
    ortVersender: false,
    staatVersender: false,
    telefonVersender: false,
    emailVersender: false,
    // Adressdaten Anmelder
    eoriAnmelder: false,
    nameAnmelder: false,
    vornameAnmelder: false,
    firmaAnmelder: false,
    strasseAnmelder: false,
    plzAnmelder: false,
    ortAnmelder: false,
    staatAnmelder: false,
    telefonAnmelder: false,
    emailAnmelder: false,
    // Lieferdaten
    ausfuhrland: false,
    bestimmungsland: false,
    bestimmungsbundesland: false,
    befoerderungsmittel: false,
    lieferbedingung: false,
    lieferort: false,
    warenort: false,
    lieferkosten: false,
    //Rechnungsdaten
    datumRechnung: false,
    gesamtbetrag: false,
    waehrung: false,
  });

  // State über falsche Inputs (müssen Zahlen sein)
  const [inputWrongError, setInputWrongError] = React.useState({
    auftragsnummer: false,
    eoriVersender: false,
    plzVersender: false,
    eoriAnmelder: false,
    plsAnmelder: false,
    lieferkosten: false,
  });

  // FieldChange für Select und String-Felder
  function handleFieldChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    setFieldValue({ ...fieldValue, [name]: value });
    setInputMissingError({ ...inputMissingError, [name]: false });
  }

  // FieldChange für Zahlen-Inputfelder
  function handleNumberFieldChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    // Validierung: nur Zahlen
    if (isNaN(value)) {
      setInputWrongError({ ...inputWrongError, [name]: true });
    } else {
      setInputWrongError({ ...inputWrongError, [name]: false });
    }

    setFieldValue({ ...fieldValue, [name]: value });
    setInputMissingError({ ...inputMissingError, [name]: false });
  }

  return (
    <>
      <h3>Zollanmeldung</h3>
      <br />

      <div>
        <Box sx={{ width: "100%" }}>
          <Stepper className="mb-3" activeStep={activeStep}>
            {steps.map((label, index) => {
              const stepProps = {};
              const labelProps = {};
              if (isStepOptional(index)) {
                labelProps.optional = (
                  <Typography variant="caption">Optional</Typography>
                );
              }
              if (isStepSkipped(index)) {
                stepProps.completed = false;
              }
              return (
                <Step key={label} {...stepProps}>
                  <StepLabel {...labelProps}>{label}</StepLabel>
                </Step>
              );
            })}
          </Stepper>
          {activeStep === steps.length ? (
            <React.Fragment>
              <Typography sx={{ mt: 2, mb: 1 }}>
                All steps completed - you&apos;re finished
              </Typography>
              <Box sx={{ display: "flex", flexDirection: "row", pt: 2 }}>
                <Box sx={{ flex: "1 1 auto" }} />
                <Button onClick={handleReset}>Zurücksetzen</Button>
              </Box>
            </React.Fragment>
          ) : (
            <React.Fragment>
              {activeStep === 0 ? (
                <>
                  <MDBContainer>
                    <AllgemeineAngaben
                      fieldValue={fieldValue}
                      setFieldValue={setFieldValue}
                      inputMissingError={inputMissingError}
                      setInputMissingError={setInputMissingError}
                      inputWrongError={inputWrongError}
                      setInputWrongError={setInputWrongError}
                      handleFieldChange={handleFieldChange}
                      handleNumberFieldChange={handleNumberFieldChange}
                    />
                  </MDBContainer>
                </>
              ) : activeStep === 1 ? (
                <>
                  <MDBContainer>
                    <Adressdaten
                      fieldValue={fieldValue}
                      setFieldValue={setFieldValue}
                      inputMissingError={inputMissingError}
                      setInputMissingError={setInputMissingError}
                      inputWrongError={inputWrongError}
                      setInputWrongError={setInputWrongError}
                      handleFieldChange={handleFieldChange}
                      handleNumberFieldChange={handleNumberFieldChange}
                    />
                  </MDBContainer>
                </>
              ) : activeStep === 2 ? (
                <>
                  <MDBContainer>
                    <Lieferdaten
                      fieldValue={fieldValue}
                      setFieldValue={setFieldValue}
                      inputMissingError={inputMissingError}
                      setInputMissingError={setInputMissingError}
                      inputWrongError={inputWrongError}
                      setInputWrongError={setInputWrongError}
                      handleFieldChange={handleFieldChange}
                      handleNumberFieldChange={handleNumberFieldChange}
                    />
                  </MDBContainer>
                </>
              ) : (
                <>
                  <MDBContainer>
                    <Rechnungsdaten
                      fieldValue={fieldValue}
                      setFieldValue={setFieldValue}
                      inputMissingError={inputMissingError}
                      setInputMissingError={setInputMissingError}
                      inputWrongError={inputWrongError}
                      setInputWrongError={setInputWrongError}
                      handleFieldChange={handleFieldChange}
                      handleNumberFieldChange={handleNumberFieldChange}
                      rechnungsposten={rechnungsposten}
                      setRechnungsposten={setRechnungsposten}
                    />
                  </MDBContainer>
                </>
              )}
              <Box sx={{ display: "flex", flexDirection: "row", pt: 2 }}>
                <Button
                  color="inherit"
                  disabled={activeStep === 0}
                  onClick={handleBack}
                  sx={{ mr: 1 }}
                >
                  Zurück
                </Button>
                <Box sx={{ flex: "1 1 auto" }} />
                {isStepOptional(activeStep) && (
                  <Button color="inherit" onClick={handleSkip} sx={{ mr: 1 }}>
                    Überspringen
                  </Button>
                )}

                {activeStep === steps.length - 1 ? (
                  <Button onClick={handleSubmit}>Abschicken</Button>
                ) : (
                  <Button onClick={handleNext}>Weiter</Button>
                )}
              </Box>
              <br />
              <br />
              <br />
            </React.Fragment>
          )}
        </Box>
      </div>
    </>
  );
}

export default Declaration;
