import React, { useState } from "react";
import { MDBContainer, MDBBtn, MDBRow, MDBCol } from "mdb-react-ui-kit";

import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";

import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select, { SelectChangeEvent } from "@mui/material/Select";
import FormHelperText from "@mui/material/FormHelperText";
import Box from "@mui/material/Box";

function Test() {
  // Autocomplete Optionen
  const options = ["Option A", "Option B"];
  // Autocomplete Input State
  const [inputValue, setInputValue] = React.useState({ autocomplete: "" });

  // State der Felder
  const [fieldValue, setFieldValue] = React.useState({
    select: "",
    autocomplete: "",
    fieldA: "",
    fieldB: "",
  });

  // State über fehlende Inputs
  const [inputMissingError, setInputMissingError] = React.useState({
    select: false,
    fieldA: false,
    fieldB: false,
    autocomplete: false,
  });

  // State über falsche Inputs
  const [inputWrongError, setInputWrongError] = React.useState({
    fieldA: false,
    fieldB: false,
  });

  // FieldChange für Select und Zahlen-Inputfelder
  function handleFieldChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    // Validierung: nur Zahlen
    if (isNaN(value)) {
      setInputWrongError({ ...inputWrongError, [name]: true });
    } else {
      setInputWrongError({ ...inputWrongError, [name]: false });
    }

    setFieldValue({ ...fieldValue, [name]: value });
    setInputMissingError({ ...inputMissingError, [name]: false });
  }

  // Validierung auf Eingabe - hier nur Pflichtfelder
  function handleSubmit() {
    let errorObject = inputMissingError;

    if (fieldValue.select === null || fieldValue.select == "") {
      errorObject = { ...errorObject, select: true };
    }

    if (fieldValue.autocomplete === null || fieldValue.autocomplete == "") {
      errorObject = { ...errorObject, autocomplete: true };
    }
    if (fieldValue.fieldA === null || fieldValue.fieldA == "") {
      errorObject = { ...errorObject, fieldA: true };
    }
    if (fieldValue.fieldB === null || fieldValue.fieldB == "") {
      errorObject = { ...errorObject, fieldB: true };
    }

    setInputMissingError(errorObject);
    console.log(fieldValue);
    return;
  }

  return (
    <div>
      <h1>Testgelände</h1>
      <div>
        <div>{`value: ${
          fieldValue.autocomplete !== null
            ? `'${fieldValue.autocomplete}'`
            : "null"
        }`}</div>
        <div>{`inputValue: '${inputValue.autocomplete}'`}</div>
        <br />

        <MDBRow>
          <MDBCol>
            <FormControl>
              <InputLabel id="demo-simple-select-label">Age</InputLabel>
              <Select
                sx={{ minWidth: 120, maxWidth: 300 }}
                name="select"
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={fieldValue.select}
                label="Select"
                error={inputMissingError.select}
                onChange={(event) => handleFieldChange(event)}
              >
                <MenuItem value="">
                  <em>Nichts</em>
                </MenuItem>
                <MenuItem value={1}>Option 1</MenuItem>
                <MenuItem value={20}>Option 2</MenuItem>
                <MenuItem value={30}>Option 3</MenuItem>
              </Select>
              {inputMissingError.select ? (
                <FormHelperText error={true}>Pflichtfeld</FormHelperText>
              ) : null}
            </FormControl>
          </MDBCol>
          <MDBCol>
            <Autocomplete
              value={fieldValue.autocomplete}
              name="autocomplete"
              onChange={(event, newValue) => {
                setFieldValue({ ...fieldValue, autocomplete: newValue });
                setInputMissingError({
                  ...inputMissingError,
                  autocomplete: false,
                });
              }}
              inputValue={inputValue.autocomplete}
              onInputChange={(event, newInputValue) => {
                setInputValue({ ...inputValue, autocomplete: newInputValue });
              }}
              id="controllable-states-demo"
              options={options}
              sx={{ width: 300 }}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Option wählen"
                  error={inputMissingError.autocomplete}
                  helperText={
                    inputMissingError.autocomplete ? "Pflichtfeld" : null
                  }
                />
              )}
            />
          </MDBCol>
          <MDBCol>
            <TextField
              id="fieldA"
              name="fieldA"
              label="Feld A"
              variant="outlined"
              error={inputMissingError.fieldA || inputWrongError.fieldA}
              helperText={
                inputMissingError.fieldA
                  ? "Pflichtfeld"
                  : inputWrongError.fieldA
                  ? "Zahl eingeben"
                  : null
              }
              onChange={(event) => handleFieldChange(event)}
            />
          </MDBCol>
          <MDBCol>
            <TextField
              id="fieldB"
              name="fieldB"
              label="Feld B"
              variant="outlined"
              error={inputMissingError.fieldB || inputWrongError.fieldB}
              helperText={
                inputMissingError.fieldB
                  ? "Pflichtfeld"
                  : inputWrongError.fieldB
                  ? "Zahl eingeben"
                  : null
              }
              onChange={(event) => handleFieldChange(event)}
            />
          </MDBCol>
        </MDBRow>
      </div>

      <MDBBtn onClick={() => handleSubmit()} color="primary" className="mt-4">
        Submit
      </MDBBtn>
    </div>
  );
}

export default Test;
