import React, { useState } from "react";
import {
  MDBContainer,
  MDBInput,
  MDBBtn,
  MDBRow,
  MDBCol,
} from "mdb-react-ui-kit";

function Settings() {
  const [oldPassword, setOldPassword] = useState();
  const [newPassword, setNewPassword] = useState();
  const [newPassword2, setNewPassword2] = useState();

  const [passwordError, setPasswordError] = useState(null);

  function handlePasswordSubmit(event) {
    event.preventDefault();
    if (oldPassword == "" || oldPassword == null) {
      setPasswordError("Aktuelles Passwort eingeben.");
      return;
    }
    if (newPassword == "" || newPassword == null) {
      setPasswordError("Neues Passwort eingeben.");
      return;
    }
    if (newPassword == "" || newPassword == null) {
      setPasswordError("Neues Passwort wiederholen.");
      return;
    }
    if (newPassword !== newPassword2) {
      setPasswordError("Neue Passwörter stimmen nicht überein.");
      return;
    }

    // Wenn eingebenes aktuelles Passwort inkorrekt ist:

    // if (oldPassword !== ) {
    //   console.log("Aktuelles Passwort inkorrekt.");
    //   setPasswordError("Aktuelles Passwort inkorrekt.");
    //   return;
    // }

    // Neues Passwort "newPassword" zum Passwort machen:
    // POST-Request
    setPasswordError("Neues Passwort erfolgreich angelegt.");
    return;
  }

  // In diesen State müssen die Accountinformationen
  // gespeichert werden
  const [account, setAccount] = useState({
    name_company: "Döner Kebab",
    name_employee: "Mustafa",
    eori: 3123123,
    street: "Kebabstr. 17",
    plz: 37073,
    city: "Göttingen",
    country: "Deutschland",
    email: "dönerdürüm@kebab.de",
    phone: "+49 163 7802210",
  });

  return (
    <div>
      <MDBContainer>
        <h3>Benutzer-Account</h3>
        <br />
        <MDBRow className="mb-2">
          <MDBCol sm="6">
            <h4 className="mb-3">Accountinformationen</h4>
            <h5>Unternehmen</h5>
            <p>
              <b>Name des Unternehmens: </b>
              <br />
              {account.name_company}
            </p>
            <p>
              <b>EORI-Nr.: </b>
              <br />
              {account.eori}
            </p>
            <p>
              <b>Straße u. Hausnr.: </b>
              <br />
              {account.street}
            </p>
            <p>
              <b>PLZ: </b>
              <br />
              {account.plz}
            </p>
            <p>
              <b>Stadt: </b>
              <br />
              {account.city}
            </p>
            <p>
              <b>Land: </b>
              <br />
              {account.country}
            </p>
            <p>
              <b>E-Mail Adresse: </b>
              <br />
              {account.email}
            </p>
            <p>
              <b>Telefonnr.: </b>
              <br />
              {account.phone}
            </p>
            <hr className="my-4" />
            <h5>Mitarbeiter</h5>
            <p>
              <b>Account-Name: </b>
              <br />
              {account.name_employee}
            </p>
          </MDBCol>
          <MDBCol sm="6">
            <h4 className="mb-3">Passwort aktualisieren</h4>
            <form onSubmit={handlePasswordSubmit} style={{ width: "300px" }}>
              <MDBInput
                value={oldPassword}
                onChange={(e) => setOldPassword(e.target.value)}
                autocomplete="new-password"
                className="mb-4"
                type="Password"
                id="newPassword"
                name="newPassword"
                label="Aktuelles Passwort"
              />
              <MDBInput
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                autocomplete="new-password"
                className="mb-3"
                type="Password"
                id="oldPassword"
                name="oldPassword"
                label="Neues Passwort"
              />
              <MDBInput
                value={newPassword2}
                onChange={(e) => setNewPassword2(e.target.value)}
                autocomplete="new-password"
                className="mb-4"
                type="Password"
                id="oldPassword2"
                name="oldPassword2"
                label="Neues Passwort wiederholen"
              />

              <MDBBtn block type="submit" className="mb-4">
                Passwort aktualisieren
              </MDBBtn>
              {passwordError == "Neues Passwort erfolgreich angelegt." ? (
                <div className="alert alert-success" role="alert">
                  {passwordError}
                </div>
              ) : passwordError !== null ? (
                <div className="alert alert-danger" role="alert">
                  {passwordError}
                </div>
              ) : null}
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </div>
  );
}

export default Settings;
