import React, { useState } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBBtn,
  MDBBadge,
} from "mdb-react-ui-kit";
import { init, createRechnung } from "../../Web3Client";
import { useParams } from "react-router-dom";
import {  } from 'json2csv'
import LZString, {  } from 'lz-string'
import useAxiosPrivate from "../../hooks/useAxiosPrivate";


var crypto = require ('crypto');
var blockchaintx="";
function InvoiceForm() {

  const axiosInstance = useAxiosPrivate();

  const [invoiceFields, setInvoiceFields] = useState({
    type:"Rechnung",
    description:"",
    blockchainID: blockchaintx,
    issue_date:"",
    invoice_text:"",
    currency:"",
    transaction:transactionIdint,
    invoiceItem:invoiceFields.invoiceItems,
    invoiceItems: [      
    ],
  });

  const addInvoiceItem = () => {
    setInvoiceFields({...invoiceFields, invoiceItems: [
      ...invoiceFields.invoiceItems, {
        product:"Bier",
        amount: 3,
        unit: "kg",
        price: "4545",
      }
    ]})
  }

  const [invoiceItemFields, setInvoiceItemFields] = useState([
    {
      "product": "",
      "amount": "",
      "unit": "",
      "price": "",
    },
  ]);

  function handleInvoiceChange(event) {
    addInvoiceItem()
    console.log(invoiceFields)
  }

  function handleItemChange(index, event) {
    const values = [...invoiceItemFields];
    values[index][event.target.name] = event.target.value;
    setInvoiceItemFields(values);
  }
  const { transactionId } = useParams();


  function handleSubmit(e) {
    e.preventDefault();
    //Blockchain Transaktion
    const { Parser } = require('json2csv');
    const parser = new Parser(invoiceItemFields);
    //convert to csv to save  Gas
    const csv = parser.parse(invoiceItemFields);
    //compress invoiceItemFields-CSV-String to save Gas
    const compressedcsv = LZString.compress(csv);
    createRechnung("2 Mark 50!", "0xbd4f99F9133149b9e29E0E2d9733aeBb41e1cB9f", invoiceFields.invoiceCurrency, invoiceFields.invoiceDate, compressedcsv)
      .then((tx) => {
        console.log("Transaktion erfolgreich: " + tx);
        blockchaintx=tx;
      })
      .catch((err) => {
        console.log("Transaktion fehlgeschlagen: "+ "  " + err +crypto.createHash('sha256').update(JSON.stringify(invoiceItemFields)).digest('hex'));
      });
    //Id of transaction to add to right transaction 
    var transactionIdint = 2//parseInt(transactionId)
    const invoice = {

    }

    console.log(invoiceItemFields)
    //Übermittel und anlegen der Rechnung
    axiosInstance
      .post('/invoice/', {

      })
      .then((res) => {
        console.log(res.data)
      })
      .catch(error => {
          console.error('There was an error!', error);
      });
  }

  function handleAddFields() {
    setInvoiceItemFields([
      ...invoiceItemFields,
      {
        "product": "",
        "amount": "",
        "unit": "",
        "price": "",
      },
    ]);
  }

  function handleRemoveFields(index) {
    const values = [...invoiceItemFields];
    // values.splice(index, 1);
    values.splice(
      values.findIndex((value) => value.index === index),
      1
    );
    setInvoiceItemFields(values);
  }

  return (
    <MDBContainer>
      <MDBRow>
        <MDBCol>
          <MDBInput
            className="mb-4"
            name="invoiceDate"
            id="invoiceDate"
            label="Rechnungsdatum"
            type="date"
            onChange={(event) => handleInvoiceChange(event)}
          />
        </MDBCol>
        <MDBCol>
          <MDBInput
            className="mb-4"
            name="invoiceCurrency"
            id="invoiceCurrency"
            label="Währung"
            type="text"
            onChange={(event) => handleInvoiceChange(event)}
          />
        </MDBCol>
      </MDBRow>
      <MDBInput
        wrapperClass="mb-4"
        textarea
        name="invoiceDescription"
        id="invoiceDescription"
        rows={3}
        label="Beschreibung (optional)"
        onChange={(event) => handleInvoiceChange(event)}
      />
      <hr />

      <form onSubmit={handleSubmit}>
        {invoiceItemFields.map((inputField, index) => (
          <div key={index}>
            <h6>Rechnungsposten {index + 1}</h6>
            <MDBInput
              className="mb-4"
              name="product"
              label="Produktname"
              type="text"
              value={inputField.product}
              onChange={(event) => handleItemChange(index, event)}
            />
            <MDBRow>
              <MDBCol>
                <MDBInput
                  className="mb-4"
                  name="amount"
                  label="Anzahl"
                  type="number"
                  value={inputField.amount}
                  onChange={(event) => handleItemChange(index, event)}
                />
              </MDBCol>
              <MDBCol>
                <MDBInput
                  className="mb-4"
                  name="unit"
                  label="Einheit"
                  type="text"
                  value={inputField.unit}
                  onChange={(event) => handleItemChange(index, event)}
                />
              </MDBCol>
              <MDBCol>
                <MDBInput
                  className="mb-4"
                  name="price"
                  label="Preis pro Enheit"
                  type="number"
                  value={inputField.price}
                  onChange={(event) => handleItemChange(index, event)}
                />
              </MDBCol>
              <MDBCol>
                <MDBBadge className="mt-2 me-4 float-end" color="primary">
                  Betrag :{" "}
                  {inputField.amount * inputField.price}{" "}
                  {invoiceFields.invoiceCurrency}
                </MDBBadge>
              </MDBCol>
            </MDBRow>
          </div>
        ))}
        <br />

        <MDBBtn className="me-3 mb-3" color="success" onClick={handleAddFields}>
          Rechnungsposten hinzufügen
        </MDBBtn>
        {invoiceItemFields.length > 1 && (
          <MDBBtn
            className="me-3 mb-3"
            color="danger"
            onClick={handleRemoveFields}
          >
            Rechnungsposten entfernen
          </MDBBtn>
        )}

        <MDBBtn className="float-end" color="primary" onClick={handleSubmit}>
          Abschicken
        </MDBBtn>
      </form>
    </MDBContainer>
  );
}

export default InvoiceForm;
