import React, { useEffect, useState } from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardHeader,
  MDBTabs,
  MDBTabsItem,
  MDBTabsLink,
  MDBBtn,
  MDBRow,
  MDBCol,
  MDBTabsPane,
  MDBTabsContent,
  MDBIcon,
  MDBBreadcrumb,
  MDBBreadcrumbItem,
} from "mdb-react-ui-kit";
import { Link } from "react-router-dom";
import useAxiosPrivate from "../hooks/useAxiosPrivate";
import { useParams } from "react-router-dom";

export default function Test3() {
  // -- DATENFELDER --

  const axiosInstance = useAxiosPrivate()

  //const { transactionId } = useParams();

  const transactionId = 18;

  useEffect(async() => {

    axiosInstance.get('declaration/?transactions='+ transactionId)
      .then( (res) => {
        setFieldValue((prev) => {
          return {
            ...prev,
              // Allgemeine Angaben
              auftragsnummer: transactionId,
              bearbeitendeDienststelle: res.data[0].custom_office,
              anmeldungArt: res.data[0].anmeldeArt,
              geschaeftArt: res.data[0].geschaeftArt,
              zahlungArt: res.data[0]?.zahlungart,
              // Adressdaten Versender
              //eoriVersender: 318237,
              // nameVersender: "",
              // vornameVersender: "",
/*               firmaVersender: "Bier Exzellente",
              strasseVersender: "Bierweg 3",
              plzVersender: 74912,
              ortVersender: "Bierhausen",
              staatVersender: "Bierland",
              telefonVersender: "+37 8339 8491238",
              emailVersender: "mehr@bier.de", */
              // Adressdaten Anmelder
/*               eoriAnmelder: 90549054,
              // nameAnmelder: "",
              // vornameAnmelder: "",
              firmaAnmelder: "BiWi",
              strasseAnmelder: "Wilhelmsplatz",
              plzAnmelder: 37073,
              ortAnmelder: "Göttingen",
              staatAnmelder: "Deutschland",
              telefonAnmelder: "+49 21298322",
              emailAnmelder: "nochmehr@bier.de", */
              // Lieferdaten
              ausfuhrland: res.data[0]?.ausfuhrland,
              bestimmungsland: res.data[0]?.bestimmungsland,
              //bestimmungsbundesland:,
              befoerderungsmittel: res.data[0]?.befoerderungsmittel,
              lieferbedingung: res.data[0]?.lieferbedingung,
              lieferort: res.data[0]?.lieferort,
              warenort: res.data[0]?.warenort,

              //Rechnungsdaten
              datumRechnung: "19.06.2022",
              gesamtbetrag: 3500,
              waehrung: "EUR",
              lieferkosten: 400,

          };
        });
      })

  }, [])

  // Auftrag Metadaten
  const [auftragMetadaten, setAuftragMetadaten] = React.useState({
    documentId: 23,
    transactionId: 18,
    documentType: "Zollanmeldung",
    dateAdded: "19.06.2022",
    author: "LG Electronics",
    freigabeStatus: true,
    description: "",
  });

  // State des Inhalts der Felder
  const [fieldValue, setFieldValue] = React.useState({ });

  const [rechnungsposten, setRechnungsposten] = React.useState([
    {
      rechnungspostenProdukt: "Bier",
      rechnungspostenAnzahl: 1400,
      rechnungspostenEinheit: "Liter",
      rechnungspostenPreisEinheit: 2.5,
    },
  ]);

  // -- Tab-Steuerung --
  // State über aktiven Tab
  const [fillActive, setFillActive] = useState("tab1");

  // Funktion zum wechseln des Tabs
  function handleFillClick(value) {
    if (value === fillActive) {
      return;
    }

    setFillActive(value);
  }

  return (
    <>
      <h3>Dokument: Zollanmeldung</h3>
      <MDBBreadcrumb>
        <MDBBreadcrumbItem>
          <Link to="/transactions">Aufträge</Link>
        </MDBBreadcrumbItem>
        <MDBBreadcrumbItem>
          <Link to={"/transactions/" + auftragMetadaten.transactionId}>
            Auftrags-ID 18
          </Link>
        </MDBBreadcrumbItem>
        <MDBBreadcrumbItem active>
          Dokumenten-ID {auftragMetadaten.documentId}
        </MDBBreadcrumbItem>
      </MDBBreadcrumb>
      <MDBCard style={{ minHeight: 600 }}>
        <MDBCardHeader>
          <MDBTabs pills className="card-header-tabs">
            <MDBTabsItem>
              <MDBTabsLink
                onClick={() => handleFillClick("tab1")}
                active={fillActive === "tab1"}
              >
                Allgemeine Angaben
              </MDBTabsLink>
            </MDBTabsItem>
            <MDBTabsItem>
              <MDBTabsLink
                onClick={() => handleFillClick("tab2")}
                active={fillActive === "tab2"}
              >
                Adressdaten
              </MDBTabsLink>
            </MDBTabsItem>
            <MDBTabsItem>
              <MDBTabsLink
                onClick={() => handleFillClick("tab3")}
                active={fillActive === "tab3"}
              >
                Lieferdaten
              </MDBTabsLink>
            </MDBTabsItem>
            <MDBTabsItem>
              <MDBTabsLink
                onClick={() => handleFillClick("tab4")}
                active={fillActive === "tab4"}
              >
                Rechnungsdaten
              </MDBTabsLink>
            </MDBTabsItem>
          </MDBTabs>
        </MDBCardHeader>
        <MDBCardBody>
          <MDBRow>
            <MDBCol className="col-md-8">
              {fillActive === "tab1" ? (
                <MDBCardTitle className="mb-4">Allgemeine Angaben</MDBCardTitle>
              ) : fillActive === "tab2" ? (
                <MDBCardTitle className="mb-4">Adressdaten</MDBCardTitle>
              ) : fillActive === "tab3" ? (
                <MDBCardTitle className="mb-4">Lieferdaten</MDBCardTitle>
              ) : (
                <MDBCardTitle className="mb-4">Rechnungsdaten</MDBCardTitle>
              )}
              <MDBCardText>
                <MDBTabsContent>
                  <MDBTabsPane show={fillActive === "tab1"}>
                    <p>
                      <b>Auftragsnummer: </b>
                      {fieldValue.auftragsnummer}
                    </p>
                    <p>
                      <b>Bearbeitende Dienststelle: </b>
                      {fieldValue.bearbeitendeDienststelle}
                    </p>
                    <p>
                      <b>Art der Anmeldung: </b>
                      {fieldValue.anmeldungArt}
                    </p>
                    <p>
                      <b>Art des Geschäfts: </b>
                      {fieldValue.geschaeftArt}
                    </p>
                    <p>
                      <b>Zahlungsart: </b>
                      {fieldValue.zahlungArt}
                    </p>
                  </MDBTabsPane>
                  <MDBTabsPane show={fillActive === "tab2"}>
                    {/* Versender Daten */}
                    <h6>
                      <u>Versender</u>
                    </h6>
                    <p>
                      <b>EORI-Nummer: </b>
                      {fieldValue.eoriVersender}
                    </p>
                    <p>
                      <b>Firma: </b>
                      {fieldValue.firmaVersender}
                    </p>
                    <p>
                      <b>Straße u. Hausnummer: </b>
                      {fieldValue.strasseVersender}
                    </p>
                    <p>
                      <b>Postleitzahl: </b>
                      {fieldValue.plzVersender}
                    </p>
                    <p>
                      <b>Postleitzahl: </b>
                      {fieldValue.ortVersender}
                    </p>
                    <p>
                      <b>Staatsangehörigkeit: </b>
                      {fieldValue.staatVersender}
                    </p>
                    <p>
                      <b>Telefonnummer: </b>
                      {fieldValue.telefonVersender}
                    </p>
                    <p>
                      <b>Telefonnummer: </b>
                      {fieldValue.emailVersender}
                    </p>
                    <br />
                    {/* Anmelder Daten */}
                    <h6>
                      <u>Empfänger / Anmelder</u>
                    </h6>
                    <p>
                      <b>EORI-Nummer: </b>
                      {fieldValue.eoriAnmelder}
                    </p>
                    <p>
                      <b>Firma: </b>
                      {fieldValue.firmaAnmelder}
                    </p>
                    <p>
                      <b>Straße u. Hausnummer: </b>
                      {fieldValue.strasseAnmelder}
                    </p>
                    <p>
                      <b>Postleitzahl: </b>
                      {fieldValue.plzAnmelder}
                    </p>
                    <p>
                      <b>Postleitzahl: </b>
                      {fieldValue.ortAnmelder}
                    </p>
                    <p>
                      <b>Staatsangehörigkeit: </b>
                      {fieldValue.staatAnmelder}
                    </p>
                    <p>
                      <b>Telefonnummer: </b>
                      {fieldValue.telefonAnmelder}
                    </p>
                    <p>
                      <b>Telefonnummer: </b>
                      {fieldValue.emailAnmelder}
                    </p>
                  </MDBTabsPane>
                  <MDBTabsPane show={fillActive === "tab3"}>
                    <p>
                      <b>Ausfuhrland: </b>
                      {fieldValue.ausfuhrland}
                    </p>
                    <p>
                      <b>Bestimmungsland: </b>
                      {fieldValue.bestimmungsland}
                    </p>
                    <p>
                      <b>Bestimmungsbundesland: </b>
                      {fieldValue.bestimmungsbundesland}
                    </p>
                    <p>
                      <b>Art des grenzüberschreitenden Beförderungsmittels: </b>
                      {fieldValue.befoerderungsmittel}
                    </p>
                    <p>
                      <b>Lieferbedingungen: </b>
                      {fieldValue.lieferbedingung}
                    </p>
                    <p>
                      <b>Lieferort: </b>
                      {fieldValue.lieferort}
                    </p>
                    <p>
                      <b>Lieferort: </b>
                      {fieldValue.warenort}
                    </p>
                  </MDBTabsPane>
                  <MDBTabsPane show={fillActive === "tab4"}>
                    <p>
                      <b>Rechnungsdatum: </b>
                      {fieldValue.datumRechnung}
                    </p>
                    <p>
                      <b>Gesamtbetrag: </b>
                      {fieldValue.gesamtbetrag} {fieldValue.waehrung}
                    </p>
                    <p>
                      <b>Lieferkosten: </b>
                      {fieldValue.lieferkosten} {fieldValue.waehrung}
                    </p>
                    <hr className="my-4" />
                    <h6>
                      <u>Rechnungsposten 1</u>
                    </h6>
                    <p>
                      <b>Produkt: </b>
                      {rechnungsposten[0].rechnungspostenProdukt}
                    </p>
                    <p>
                      <b>Anzahl: </b>
                      {rechnungsposten[0].rechnungspostenAnzahl}
                    </p>
                    <p>
                      <b>Einheit: </b>
                      {rechnungsposten[0].rechnungspostenEinheit}
                    </p>
                    <p>
                      <b>Preis pro Einheit: </b>
                      {rechnungsposten[0].rechnungspostenPreisEinheit.toFixed(
                        2
                      )}{" "}
                      {fieldValue.waehrung}
                    </p>
                    <hr className="my-4" />

                    {/* Berechnung Zollabgabe */}
                    <p className="ms-4">
                      <b>Zollwert</b> (Geamtbetrag + Lieferkosten) ={" "}
                      {(
                        Number(fieldValue.gesamtbetrag) +
                        Number(fieldValue.lieferkosten)
                      ).toFixed(2)}{" "}
                      {fieldValue.waehrung}
                    </p>
                    <p className="ms-4">
                      <b>Zollbetrag</b> (Pauschaler Zollsatz 4 %) ={" "}
                      {(
                        (Number(fieldValue.gesamtbetrag) +
                          Number(fieldValue.lieferkosten)) *
                        Number(0.04)
                      ).toFixed(2)}{" "}
                      {fieldValue.waehrung}
                    </p>
                    <p className="ms-4">
                      <b>Einfuhrumsatzsteuer(EUSt)-Wert</b> (Zollwert +
                      Zollbetrag) ={" "}
                      {(
                        (Number(fieldValue.gesamtbetrag) +
                          Number(fieldValue.lieferkosten)) *
                        Number(1.04)
                      ).toFixed(2)}{" "}
                      {fieldValue.waehrung}
                    </p>
                    <p className="ms-4">
                      <b>EUSt-Betrag</b> (EUSt-Wert * EUSt-Satz 19 %) ={" "}
                      {(
                        (Number(fieldValue.gesamtbetrag) +
                          Number(fieldValue.lieferkosten)) *
                        Number(1.04) *
                        Number(0.19)
                      ).toFixed(2)}{" "}
                      {fieldValue.waehrung}
                    </p>
                    <p className="ms-4">
                      <b>Prognostizierte Gesamtabgabe</b> (Zollbetrag +
                      EUSt-Betrag) ={" "}
                      <u>
                        {(
                          (Number(fieldValue.gesamtbetrag) +
                            Number(fieldValue.lieferkosten)) *
                            Number(0.04) +
                          (Number(fieldValue.gesamtbetrag) +
                            Number(fieldValue.lieferkosten)) *
                            Number(1.04) *
                            Number(0.19)
                        ).toFixed(2)}{" "}
                        {fieldValue.waehrung}
                      </u>
                    </p>
                  </MDBTabsPane>
                </MDBTabsContent>
              </MDBCardText>
            </MDBCol>
            <MDBCol
              className="col-md-4"
              style={{ border: "3px solid #cccccc", height: 500 }}
            >
              <p className="mt-4 ms-3">
                <b>Auftrags-ID: </b>
                {auftragMetadaten.transactionId}
              </p>
              <p className="ms-3">
                <b>Dokumenten-ID: </b>
                {auftragMetadaten.documentId}
              </p>
              <p className="ms-3">
                <b>Dokumententyp: </b>
                {auftragMetadaten.documentType}
              </p>
              <p className="ms-3">
                <b>Hochgeladen am: </b>
                {auftragMetadaten.dateAdded}
              </p>
              <p className="ms-3">
                <b>Hochgeladen von: </b>
                {auftragMetadaten.author}
              </p>

              <p className="ms-3">
                <b>Freigabe: </b>
                {auftragMetadaten.freigabeStatus
                  ? "Für Zoll freigegeben"
                  : "Nicht für Zoll freigegeben"}
              </p>
              <p className="ms-3 mb-5">
                <b>Beschreibung: </b>
                {auftragMetadaten.description == "" ? (
                  <i>Es liegt keine Beschreibung vor.</i>
                ) : (
                  auftragMetadaten.description
                )}
              </p>

              <MDBBtn disabled className="ms-3">
                <MDBIcon fas icon="share" size="lg" className="me-1" />
                Freigeben
              </MDBBtn>
              <MDBBtn className="ms-3 mt-2">
                <MDBIcon fas icon="download" size="lg" className="me-1" />
                Download
              </MDBBtn>
            </MDBCol>
          </MDBRow>
        </MDBCardBody>
      </MDBCard>
    </>
  );
}
