import React from "react";
import { MDBContainer, MDBCol, MDBRow, MDBSpinner} from "mdb-react-ui-kit";
import Partner from "../components/Partners/Partner";
import PartnersModal from "../components/Partners/PartnersModal";
import axiosInstance from "../axiosApi";
import PartnerRequests from "../components/Partners/PartnerRequests";
import CompanyModal from "../components/Partners/CompanyModal";
import { Form } from "react-bootstrap";
import { useState, useEffect } from "react";
function Partners () {
/*   constructor(props) {
    super(props);
    // Props werden von übergeordneter Klasse geerbt, können aber nicht
    // verändert werden
    //
    // state wird verwendet um Zustand der Seite durch setState() aktualisieren
    // zu können
    this.state = {
      partners: [],
    };
  } */

   const [partners, setPartners] = useState()
   const [isLoading, setIsLoading] = useState(true)

  useEffect(async() => {
    const link = '/partnership/?is_archived=false&confirmed=true'
    await axiosInstance
      .get(link)
      .then((res) => {
        console.log(res.data);
        setPartners(res.data)
        setIsLoading(false)
      });
  }, []); 


    return (
      <>
      {isLoading ? (
        <MDBSpinner grow>
        <span className="visually-hidden">Lädt..</span>
      </MDBSpinner>
      ) : (

      <div>
        <MDBContainer>
          <MDBRow>
            <MDBCol>
              <h3>Handelsbeziehungen</h3>
            </MDBCol>

            <MDBCol>
              <CompanyModal />
            </MDBCol>
          </MDBRow>
          <br />
          <PartnerRequests />

          <nav className="my-3 navbar navbar-expand-lg navbar-light bg-light">
            <MDBContainer>
              <Form.Select
                style={{
                  minWidth: 250,
                  maxWidth: 250,
                }}
              >
                <option value={"UpdatedDesc"}>Zuletzt geändert 🠗</option>
                <option value={"UpdatedAsc"}>
                  Zuletzt geändert aufsteigend 🠕
                </option>
                <option value={"DescriptionDesc"}>
                  Beschreibung absteigend 🠗
                </option>
                <option value={"DescriptionAsc"}>
                  Beschreibung aufsteigend 🠕
                </option>
                <option value={"AddedDesc"}>Hinzugefügt am absteigend 🠗</option>
                <option value={"AddedAsc"}>Hinzugefügt am aufsteigend 🠕</option>
              </Form.Select>{" "}
              <div className="float-left">
                {partners.length} Partner
              </div>
              <PartnersModal 
                partnerships = {partners }
              />
              {/* <TransactionModal setreload={setreload} reload={reload} /> */}
            </MDBContainer>
          </nav>
          {partners.map((partner) => (
            <React.Fragment>
              <Partner
                key={partner.partner.eori_nr}
                id={partner.partner.eori_nr}
                id_relation={partner.id}
                title={partner.partner.name}
                added={partner.date_added}
                addressStreet={partner.partner.street}
                addressCity={partner.partner.city}
                addressCountry={partner.partner.country_code}
                email={partner.partner.email}
                phone={partner.partner.phone}
                eori={partner.partner.eori_nr}
              />
              <br />
            </React.Fragment>
          ))}
        </MDBContainer>
      </div>
      )}
      </>
    );
          }




export default Partners;
