import React from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBBtn,
  MDBInput,
} from "mdb-react-ui-kit";
import InvoiceItemForm from "../components/Documents/InvoiceItemForm";
import { init, createRechnung } from "../Web3Client";
import axiosInstance from "../axiosApi.js";

class Documents extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      invoiceTitle: "",
      invoiceQuantity: "",
      invoiceValue: "",
      invoiceDate: "",
      invoiceRecipient: "",
      inputs: ["input-0"],
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    this.handleInputChange = this.handleInputChange.bind(this);

    this.handleItem = this.handleItem.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }
  handleSubmit(event) {
    //blockchain Transaktion
    event.preventDefault();
    createRechnung(this.state.invoiceValue, this.state.invoiceRecipient)
      .then((tx) => {
        console.log(tx);
        alert(
          "RechnungsID ist " +
            tx +
            ". Der gespeicherte Rechnungsbetrag ist " +
            this.state.invoiceValue +
            ". Rechnungsempfänger/Importeur hat den Pub. Key " +
            this.state.invoiceRecipient +
            ". Rechnung muss noch vom Empfänger bestätigt werden."
        );
      })
      .catch((err) => {
        console.log(err);
      });

    //Datenbank Transaktion...
    axiosInstance
      .post("Invoice/", {
        invoiceItem: [
          {
            product: "Richtig gutes Bier",
            unit: "Stück",
            amount: 48,
            price: 15,
          },
        ],
        invoice_text: "Testrechnung API",
        currency: "EUR",
        transaction: 1,
      })
      .then(console.log("Data send"));

    alert(this.state.invoiceTitle + " wurde in den State geschrieben.");
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  }

  handleItem(event) {
    console.log(event.target.value);
  }

  render() {
    return (
      <div>
        <MDBContainer>
          <MDBRow>
            <MDBCol>
              <h3>Dokumente</h3>
            </MDBCol>
            <MDBCol>
              <MDBBtn className="float-end" color="success">
                Dokument hinzufügen
              </MDBBtn>
            </MDBCol>
          </MDBRow>

          <br />
          <p>Rechnung hinzufügen:</p>

          <form onSubmit={this.handleSubmit}>
            <MDBInput
              className="mb-4"
              name="invoiceTitle"
              type="text"
              label="Handelsgut"
              onChange={this.handleInputChange}
            />
            <MDBInput
              className="mb-4"
              name="invoiceQuantity"
              type="number"
              label="Menge"
              onChange={this.handleInputChange}
            />
            <MDBInput
              className="mb-4"
              name="invoiceValue"
              type="number"
              label="Warenwert"
              onChange={this.handleInputChange}
            />
            <MDBInput
              className="mb-4"
              name="invoiceDate"
              type="date"
              label="Rechnungsdatum"
              onChange={this.handleInputChange}
            />
            <MDBInput
              className="mb-4"
              name="invoiceRecipient"
              type="text"
              label="Rechnungsempfänger/Importeur (bitte Pub. Key angeben)"
              onChange={this.handleInputChange}
            />
            <MDBBtn type="submit" block>
              Abschicken
            </MDBBtn>
          </form>

          <br />
          <h1>Rechnung neu:</h1>
          <br />

          <MDBRow>
            <MDBCol>
              <MDBInput
                className="mb-4"
                id="invoiceDate"
                label="Rechnungsdatum"
                type="date"
                required
              />
            </MDBCol>
            <MDBCol>
              <MDBInput
                className="mb-4"
                id="invoiceCurrency"
                label="Währung"
                type="text"
                required
              />
            </MDBCol>
          </MDBRow>
          <MDBInput
            wrapperClass="mb-4"
            textarea
            id="invoiceText"
            rows={3}
            label="Beschreibung (optional)"
          />
          <hr />
          <div id="dynamicInput">
            {this.state.inputs.map((input) => (
              <InvoiceItemForm
                key={input}
                id={input}
                onChange={this.handleItem}
              />
            ))}
          </div>
          <MDBBtn className="me-4" color="success">
            Abschicken
          </MDBBtn>
          <MDBBtn onClick={() => this.appendInput()}>
            Neuer Rechnungsposten
          </MDBBtn>
        </MDBContainer>
      </div>
    );
  }

  appendInput() {
    var newInput = `input-${this.state.inputs.length}`;
    this.setState((prevState) => ({
      inputs: prevState.inputs.concat([newInput]),
    }));
  }
}

export default Documents;
