import React, { useEffect, useState } from "react";
import {
  MDBContainer,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBBtn,
  MDBCardHeader,
  MDBRow,
  MDBCol,
  MDBIcon,
} from "mdb-react-ui-kit";
import Toast from "react-bootstrap/Toast";

import useAuth from "../hooks/useAuth";

import DashboardDocumentCard from "../components/Dashboard/DashboardDocumentCard";
import axiosInstance from "../axiosApi";
import { Backdrop } from "@mui/material";
import { CircularProgress } from "@mui/material";
import { Link } from "react-router-dom";


function Dashboard() {
  // Transaktionen
  const [transactionsData, setTransactionsData] = useState([]);

  // Dokumente
  const [documentsData, setDocumentsData] = useState([]);

  const [openRequests, setopenRequests] = useState([]);

  const [isLoading, setIsLoading] = useState(true); 

  const { auth } = useAuth();



 async function getDashboardData () {

    //Abfragen der Transaktionen
    await axiosInstance.get("transactions/").then((res) => {
      setTransactionsData(res.data);
    }).catch((err) => {console.log(err)});

    //Abfragen der Dokumente 
    await axiosInstance.get("documents/").then((res) => {
      setDocumentsData(res.data);
    }).catch((err) => {console.log(err)});
    //Abfragen der unbestätigten Partnerschaften bei denen wir Partner 2 sind 

    await axiosInstance
    .get(`partnership/?partner2=${auth.eori_nr}&confirmed=false`)
    .then((res) => {
      setopenRequests(res.data);

    })
    .catch(function (error) {
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.log(error.response.data);
        console.log(error.response.status);
        console.log(error.response.headers);
      } else if (error.request) {
        // The request was made but no response was received
        // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
        // http.ClientRequest in node.js
        console.log(error.request);
      } else {
        // Something happened in setting up the request that triggered an Error
        console.log("Error", error.message);
      }
    });
    setIsLoading(false)
  }

  // Transaktionen abrufen
  useEffect(() => {
    getDashboardData()
  }, []);

  // Dokumente abrufen
  useEffect(() => {
    axiosInstance.get("documents/").then((res) => {
      setDocumentsData(res.data);
    });
  }, []);

  return (
    <>
    {isLoading ? ( 
      <Backdrop
          sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
          open={isLoading}
        >
          <CircularProgress color="inherit" />
      </Backdrop>

    ) : (
      <div>
        <MDBContainer>
          <h3>Dashboard</h3>
          <br />
          <MDBRow>
            <MDBCol className="col-md-6 mb-4">
              <MDBCard style={{ height: "500px" }}>
                <MDBCardHeader style={{ background: "#fbfbfb" }}>
                  <b>Benachrichtigungen</b>
                </MDBCardHeader>
                <MDBCardBody className="overflow-scroll">
                  <MDBCardText>
                    <Toast className="mb-2" bg="light" style={{ width: "100%" }}>
