import React, { useState } from "react";
import { MDBContainer, MDBInput, MDBRow, MDBCol } from "mdb-react-ui-kit";

import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";

import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select, { SelectChangeEvent } from "@mui/material/Select";
import FormHelperText from "@mui/material/FormHelperText";

function AllgemeineAngaben(props) {
  return (
    <>
      <h5 className="mb-4">Allgemeine Angaben</h5>

      <MDBRow className="mb-3">
        <MDBCol>
          <TextField
            // Für automatische Übernahme aus DB:
            disabled
            value={props.fieldValue.auftragsnummer}
            id="auftragsnummer"
            name="auftragsnummer"
            label="Auftragsnummer*"
            variant="outlined"
            error={
              props.inputMissingError.auftragsnummer ||
              props.inputWrongError.auftragsnummer
            }
            helperText={
              props.inputMissingError.auftragsnummer
                ? "Pflichtfeld"
                : props.inputWrongError.auftragsnummer
                ? "Zahl eingeben"
                : null
            }
            onChange={(event) => props.handleNumberFieldChange(event)}
          />
        </MDBCol>
        <MDBCol>
          <TextField
            id="bearbeitendeDienststelle"
            name="bearbeitendeDienststelle"
            label="Bearbeitende Dienststelle*"
            variant="outlined"
            error={
              props.inputMissingError.bearbeitendeDienststelle ||
              props.inputWrongError.bearbeitendeDienststelle
            }
            helperText={
              props.inputMissingError.bearbeitendeDienststelle
                ? "Pflichtfeld"
                : null
            }
            onChange={(event) => props.handleFieldChange(event)}
          />
        </MDBCol>
      </MDBRow>
      <MDBRow className="mb-3">
        <MDBCol>
          <FormControl>
            <InputLabel id="anmeldungArt">Art der Anmeldung*</InputLabel>
            <Select
              sx={{ minWidth: 300, maxWidth: 900 }}
              name="anmeldungArt"
              labelId="anmeldungArt"
              id="anmeldungArt"
              value={props.fieldValue.anmeldungArt}
              label="Select"
              error={props.inputMissingError.anmeldungArt}
              onChange={(event) => props.handleFieldChange(event)}
            >
              <MenuItem value="">
                <em>Nichts</em>
              </MenuItem>
              <MenuItem value={"CO"}>
                CO: Warenverkehr zwischen Mitgliedsstaaten der Gemeinschaft
              </MenuItem>
              <MenuItem value={"EU"}>
                EU: Warenverkehr zwischen der Gemeinschaft und den
                EFTA-/Visegrad-Ländern
              </MenuItem>
              <MenuItem value={"IM"}>
                IM: Warenverkehr zwischen der Gemeinschaft und anderen
                Drittländern als den EFTA-Ländern
              </MenuItem>
            </Select>
            {props.inputMissingError.anmeldeart ? (
              <FormHelperText error={true}>Pflichtfeld</FormHelperText>
            ) : null}
          </FormControl>
        </MDBCol>
      </MDBRow>
      <MDBRow className="mb-3">
        <MDBCol>
          <TextField
            sx={{ minWidth: 300, maxWidth: 900 }}
            id="geschaeftArt"
            name="geschaeftArt"
            label="Art des Geschäfts*"
            variant="outlined"
            error={
              props.inputMissingError.geschaeftArt ||
              props.inputWrongError.geschaeftArt
            }
            helperText={
              props.inputMissingError.geschaeftArt ? "Pflichtfeld" : null
            }
            onChange={(event) => props.handleFieldChange(event)}
          />
        </MDBCol>
        <MDBCol>
          <TextField
            sx={{ minWidth: 300, maxWidth: 900 }}
            id="zahlungArt"
            name="zahlungArt"
            label="Zahlungsart (optional)"
            variant="outlined"
            // error={
            //   props.inputMissingError.zahlungArt ||
            //   props.inputWrongError.zahlungArt
            // }
            // helperText={
            //   props.inputMissingError.zahlungArt ? "Pflichtfeld" : null
            // }
            onChange={(event) => props.handleFieldChange(event)}
          />
        </MDBCol>
      </MDBRow>
    </>
  );
}

export default AllgemeineAngaben;
