import React, { useEffect, useState } from "react";
import {
  MDBContainer,
  MDBNavbar,
  MDBBtn,
  MDBIcon,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBDropdownLink,
  MDBDropdownDivider,
} from "mdb-react-ui-kit";

import { Link } from "react-router-dom";

import useAuth from "../../hooks/useAuth";
import useAxiosPrivate from "../../hooks/useAxiosPrivate";

export default function TopNavigation() {

  const { auth } = useAuth();
  const username = auth.username;
  const company = auth.company;


  const logout = () => {
    localStorage.removeItem("username");
    localStorage.removeItem("company");
    localStorage.removeItem("refresh_token");
    localStorage.removeItem("access_token");
    alert("Sie werden ausgeloggt.");
    window.location.reload(false);
  };


  const [notifications, setNotification] = useState();

  const axiosInstance = useAxiosPrivate()

  // Suchen State
  const [searchTerm, setSearchTerm] = useState();

  useEffect(() => {
    axiosInstance.get('/alerts').then((res) => {
      setNotification(res.data)
    })    
  }, [])

  useEffect(() => {
    console.log(notifications)
  },[])

  return (
    <>
      <MDBNavbar
        style={{ marginLeft: 64 }}
        fixed="top"
        expand="lg"
        light
        bgColor="dark"
      >
        <MDBContainer fluid>
          <div className="d-flex flex-row">
            <form className="d-flex input-group w-auto">
              <input
                style={{ width: 300 }}
                type="search"
                className="form-control"
                placeholder="Suchbegriff eingeben"
                aria-label="Search"
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <Link to={"/search?q=" + searchTerm} className="btn btn-primary">
                Suchen
              </Link>
              {/* <MDBBtn color="primary">Suchen</MDBBtn> */}
            </form>
          </div>
          <div className="d-flex flex-row">
            <MDBDropdown className="mx-2">
              {/* Anzahl Benachrichtungen */}
              <span class="mb-2 badge bg-primary me-2">0</span>
              <MDBDropdownToggle outline size="sm">
                <MDBIcon size="2x" className="ms-1" far icon="bell" />
              </MDBDropdownToggle>
              <MDBDropdownMenu className="p-3 text-muted">
                <p className="mt-3 mx-3">Keine Benachrichtigungen.</p>
              </MDBDropdownMenu>
            </MDBDropdown>
            <MDBDropdown>
              <MDBDropdownToggle outline size="sm">
                <MDBIcon size="2x" className="ms-1" far icon="user-circle" />
              </MDBDropdownToggle>
              <MDBDropdownMenu className="text-muted">
                <p className="mt-3 mx-3">
                  <b>Organisation: </b>
                  {company}
                </p>
                <p className="mt-3 ms-3">
                  <b>Mitarbeiter: </b>
                  {username}
                </p>
                <MDBDropdownItem>
                  <MDBDropdownItem>
                    <MDBDropdownDivider />
                  </MDBDropdownItem>
                  <MDBDropdownLink onClick={logout}>
                    <MDBIcon
                      fas
                      icon="sign-out-alt"
                      size="lg"
                      className="me-2"
                    />
                    Logout
                  </MDBDropdownLink>
                </MDBDropdownItem>
                <MDBDropdownItem>
                  <MDBDropdownLink>
                    <MDBIcon fas icon="cog" size="lg" className="me-2" />
                    Account
                  </MDBDropdownLink>
                </MDBDropdownItem>
              </MDBDropdownMenu>
            </MDBDropdown>
          </div>
        </MDBContainer>
      </MDBNavbar>
      <br />
    </>
  );
}
