import React, { useState, useEffect } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBBtn,
} from "mdb-react-ui-kit";
import useAxiosPrivate from "../../hooks/useAxiosPrivate";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";



function PartnerForm() {
  const [eori_nr, setEoriNr] = useState("");
  const [street, setStreet] = useState("");
  const [city_code, setZipCode] = useState("");
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [city, setCity] = useState("");
  const [country_code, setCountry] = useState("");
  const [companies, setCompanies] = useState([]);
  const [requestedCompany, setrequestedCompany] = useState("")
  const axiosPrivate = useAxiosPrivate();


  //useffect Abfragen der Firmemliste

  useEffect(() => {
    axiosPrivate.get("companies/").then((res) => {
      console.log(res.data);
      setCompanies(res.data);
    });

  }, [])

  useEffect(() => {
    console.log(companies)

  }, [companies])



  function handleSubmit(e) {
    e.preventDefault();
    //firma hinzufügen
    axiosInstance
      .post("/partnership/", { added_partner: requestedCompany })
      .then(console.log(requestedCompany))
      .catch((error) => {
        this.setState({ errorMessage: error.message });
        console.error("There was an error!", error);
      })

    //firma als partner hinterlegen
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <MDBContainer>
          <Autocomplete
                  className="mt-1"
                  disablePortal
                  id="combo-box-companies"
                  options={companies}
                  //choose name as label for ui
                  getOptionLabel={(option) => option.companies.name}
                  //when changed, save choosen Partner
                  onChange={(event, value) => {
                    setrequestedCompany(value.id);
                  }}
                  sx={{ width: 320 }}
                  renderInput={(params) => (
                    <TextField {...params} label="Empfänger der Partnerschaftsanfrage" />
                  )}
                />
          <div className="d-grid mx-auto">
            <MDBBtn className="mt-3" type="submit" color="success">
              Anfrage abschicken
            </MDBBtn>
          </div>
        </MDBContainer>
      </form>
    </>
  );
}

export default PartnerForm;
