import React, {useEffect, useState} from "react";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardFooter,
  MDBCardSubTitle,
  MDBCardText,
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBPopover,
  MDBPopoverBody,
} from "mdb-react-ui-kit";
import { Link } from "react-router-dom";
import { getZollanmeldung } from "../../Web3Client.js";
import useAxiosPrivate from "../../hooks/useAxiosPrivate.js";
import axios from "axios";





function ZollDocumentTransaction(props) {


  const axiosInstance = useAxiosPrivate()

  const [isVerified, setIsVerified] = useState(false);


  useEffect(() => {
    if (props.declarationData.status!="PE") {
      setIsVerified(true)
    }
  }, [])


  async function abgleichBlockchain(_blockchain_zid) {

    var crypto = require('crypto');  
    console.log("Zollanmeldung ID:" + _blockchain_zid);
    console.log(declarationData)

    await getZollanmeldung(_blockchain_zid)
    .then((tx) => {
      console.log(tx)
      console.log("Gesamtbetrag Zollanmeldung: "+tx[0].gesamtbetrag+", "+props.declarationData.invoice.total_value)
      console.log("Zollwert: "+tx[0].zollwert +", "+ (props.declarationData.invoice.transport_costs + props.declarationData.invoice.total_value))
      console.log("Lieferbedingungen: "+tx[0].incoterms+", "+ props.declarationData.invoice.lieferbedingung)
      console.log("Exporteur : "+tx[0].exporteur+", "+ props.declarationData.exporteur.publickey)
      console.log("Importeur: "+tx[0].importeur+", "+ props.declarationData.importeur.publickey)
      console.log("Zoll: "+tx[0].zoll+", "+ props.declarationData.customs_office.publickey)
      console.log("Anmelder (Importeur): "+tx[0].anmelder+", "+ props.declarationData.importeur.publickey)
      console.log("Gesamtbetrag Rechnung: "+tx[1].gesamtbetrag+", "+ props.declarationData.invoice.total_value)
      console.log("Währung: "+tx[1].waehrung+", "+ props.declarationData.invoice.total_value)
      console.log("Exporteur Rechnung: "+tx[1].exporteur+", "+ props.declarationData.exporteur.publickey)
      console.log("Importeur Rechnung: "+tx[1].importeur+", "+ props.declarationData.importeur.publickey)
      console.log("Rechnungsdatum: "+tx[1].datum+", "+ props.declarationData.invoice.issue_date)
      console.log("Hash RPosten: "+tx[1].rechnungsposten+", "+ crypto.createHash('sha512').update(props.declarationData.invoice.invoiceItems).digest('hex'))
      console.log("Lieferkosten: "+tx[1].lieferkosten+", "+ props.declarationData.invoice.transport_costs)
      if(tx[0].gesamtbetrag==props.declarationData.invoice.total_value
        && tx[0].zollwert== (props.declarationData.invoice.transport_costs + props.declarationData.invoice.total_value)
        && tx[0].lieferkosten== props.declarationData.invoice.transport_costs
        && tx[0].incoterms== props.declarationData.invoice.lieferbedingung
        && tx[0].exporteur== props.declarationData.exporteur.publickey
        && tx[0].importeur== props.declarationData.importeur.publickey
        && tx[0].zoll== props.declarationData.customs_office.publickey
        && tx[0].anmelder== props.declarationData.importeur.publickey
        && tx[1].gesamtbetrag== props.declarationData.invoice.total_value
        && tx[1].waehrung== props.declarationData.invoice.total_value
        && tx[1].exporteur== props.declarationData.exporteur.publickey
        && tx[1].importeur== props.declarationData.importeur.publickey
        && tx[1].datum== props.declarationData.invoice.issue_date
        && tx[1].rechnungsposten== crypto.createHash('sha512').update(props.declarationData.invoice.invoiceItems).digest('hex')
        && tx[1].lieferkosten== props.declarationData.invoice.transport_costs
        ){
          return true;
      }
      else{return false;}
    })
    .catch((err) => {
      console.log(err);
      return false;
    });
  }


  async function handleVerification() {
    //axiosInstance.patch('declaration_auth/' + props.declarationData.id + '/', {'status': 'VF'})
    //setIsVerified(true)
    var t=await abgleichBlockchain(props.declarationData.blockchain_zid);
    if (t==true) {
      //event Haken erscheint
      alert("Realtalk: Dieses System ist das geilste der Welt");
    }else{alert("Ihr Looser!");}
  }
  function handleConfirmation() {
    axiosInstance.patch('declaration_auth/' + props.declarationData.id + '/', {'status': 'AC'})
  }

  function handleReject() {
    axiosInstance.patch('declaration_auth/' + props.declarationData.id + '/', {'status': 'RJ'})
  }

  return (
    <div>
      <MDBCard>
        <MDBCardBody>
          <MDBContainer>
            <MDBRow>
              <MDBCol>
                <MDBCardTitle>
                  {props.declarationData.transaction.description}
                </MDBCardTitle>
              </MDBCol>
            </MDBRow>
            <br />
          </MDBContainer>
          <MDBContainer>
            <MDBRow>
              <MDBCol>
                <MDBCardText>
                  <b>Versender</b>
                  <br />
                  {props.declarationData.exporteur.name}
                  <br />
                  {props.declarationData.exporteur.street}
                  <br />
                  {props.declarationData.exporteur.city_code}{" "}
                  {props.declarationData.exporteur.city},{" "}
                  {props.declarationData.exporteur.country_code}
                </MDBCardText>
              </MDBCol>
              <MDBCol>
                <MDBCardText>
                  <b>Empfänger / Anmelder</b>
                  <br />
                  {props.declarationData.importeur.name}
                  <br />
                  {props.declarationData.importeur.street}
                  <br />
                  {props.declarationData.importeur.city_code} {props.declarationData.importeur.city}, {props.declarationData.importeur.country_code}
                </MDBCardText>
              </MDBCol>
              <MDBCol>
                <b>Anzahl Dokumente: </b>
                {props.numberDocuments}
                <br />
                <br />
                <b>Verifikation: </b>Ausstehend (STATISCH)
                <br />
                <b>Zollbescheid: </b>Ausstehend (STATISCH)
              </MDBCol>
            </MDBRow>
            <br />
            <MDBRow>
              <MDBCol md="8" lg="9">
                <div className="alert alert-warning" role="alert">
                  Status: {props.declarationData.status}
                </div>
              </MDBCol>
              <MDBCol md="4" lg="3">
                <div className="float-end">
                  {isVerified ? (
                      <MDBBtn  className="my-3">
                      Daten sind verifiziert
                    </MDBBtn> ) : (
                  <MDBBtn onClick={() => handleVerification()} className="my-3">
                    Daten verifizieren
                  </MDBBtn>
                    )}
                </div>
              </MDBCol>
            </MDBRow>
            <MDBRow>
              <MDBBtn
                className="mx-3 mb-3"
                color="success"
                style={{ width: 300 }}
                onClick={() => handleConfirmation()}
                disabled
              >
                Anmeldung Bestätigen
              </MDBBtn>
              <MDBBtn
                color="danger"
                className="mx-3 mb-3"
                style={{ width: 300 }}
                disabled
                onClick={() => handleReject()}
              >
                Anmeldung Ablehnen
              </MDBBtn>
            </MDBRow>
          </MDBContainer>
          <MDBCardFooter>
            <MDBRow>
              <MDBCol md="4">
                Hinzugefügt: {props.declarationData.transaction.date_added}
              </MDBCol>
              <MDBCol md="4">
                Zuletzt aktualisiert:{" "}
                {props.declarationData.transaction.date_processed}
              </MDBCol>
            </MDBRow>
          </MDBCardFooter>
        </MDBCardBody>
      </MDBCard>
    </div>
  );
}

export default ZollDocumentTransaction;
