import React from 'react'
import { useRef, useState, useEffect } from 'react';
import axiosInstance from '../axiosApi';
import useAuth from '../hooks/useAuth';
import { Link, useNavigate, useLocation } from 'react-router-dom';


import {
  MDBInput,
  MDBCol,
  MDBRow,
  MDBContainer,
  MDBBtn,
} from "mdb-react-ui-kit";
import axios from 'axios';

const LOGIN_URL = '/api/token/';

const LoginNew = () => {

/*   const userRef = useRef();
  const errRef = useRef(); */

  const  { setAuth } = useAuth();

  const navigate = useNavigate();
  const location = useLocation();
  const from = location.state?.from?.pathname || "/";


  const [user, setUser] = useState(''); 
  const [pwd, setPwd] = useState(''); 
  const [errMsg, setErrMsg] = useState(''); 
  const [sucess, setSucess] = useState(false);

/*   useEffect(() => {
    userRef.current.focus();
  }, []) */


  useEffect(() => {
    setErrMsg('');
  }, [user, pwd])


  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axiosInstance.post(
                            LOGIN_URL, 
                            JSON.stringify({username: user,password: pwd}),
                            {
                              headers: {'Content-Type':'application/json'},
                            }
                      );
      console.log(JSON.stringify(response?.data));
      const accesToken = response?.data?.access;
      const roles =  response?.data?.roles; 
      const company =  response?.data?.company; 

      //Speichern der Daten für künftige Anfragen 

      localStorage.setItem("access_token", accesToken);
      localStorage.setItem("refresh_token", response?.data?.refresh);
      //speichern von Nutzernamen und Firma im lokalen Speicher
      localStorage.setItem("username", response?.data?.user);
      localStorage.setItem("company", company);
      setAuth({user,pwd,roles, company, accesToken}); 
      setUser('');
      setPwd('');
      setSucess(true);
      navigate(from, { replace: true });

    } catch (err) {
      if (!err?.response) {
              setErrMsg('No Server Response');
          } else if (err.response?.status === 400) {
              setErrMsg('Missing Username or Password');
          } else if (err.response?.status === 401) {
              setErrMsg('Unauthorized');
          } else {
              setErrMsg('Login Failed');
        }


    }


  }



  return (
    <>
      {sucess ? (
          <section>
              <h1> Du bist eingeloggt</h1>
              <br />
              <p>
                <a href="#">Zur Startseite</a>
              </p>
          </section>

      ):(
    <section> 
      <div>
        <p className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
        <MDBContainer>
            <MDBRow className="d-flex justify-content-center">
              <MDBCol className="col-md-4">
              <form onSubmit={handleSubmit}>
                  <MDBInput
                    className="mb-4"
                    type="username"
                    id="username"
                    label="Email address"
                    name="username"

                    onChange={(e) => setUser(e.target.value)}
                  />

                  <MDBInput
                    className="mb-4"
                    type="password"
                    id="password"
                    label="Password"
                    name="password"
                    onChange={(e) => setPwd(e.target.value)}
                  />

                  <MDBBtn type="submit" value="Submit">
                    Einloggen
                  </MDBBtn>
                </form>
                <br />
              </MDBCol>
            </MDBRow>
          </MDBContainer> 
      </div>
    </section>
      )}
    </>      
  )
  }

export default LoginNew
