import React, { useState } from "react";
import {
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBTabs,
  MDBTabsItem,
  MDBTabsLink,
  MDBTabsContent,
  MDBTabsPane,
} from "mdb-react-ui-kit";
import InvoiceForm from "./InvoiceForm";
import OtherDocumentsForm from "./OtherDocumentsForm";
import axios from "axios";

function DocumentsModal(props) {
  // Modal-State wird in Documents gehalten und über props manipuliert.

  const [fillActive, setFillActive] = useState("tab1");

  function handleFillClick(value) {
    if (value === fillActive) {
      return;
    }

    setFillActive(value);
  }

  const [selectedFile, setSelectedFile] = useState(null);
  function handleSubmit(e) {
    e.preventDefault();
    const formData = new FormData();
    formData.append("file", selectedFile);
    formData.append("transaction", 1);
    formData.append("description", "Hallo Welt");
    formData.append("type", "Frachtbrief");
    axios
      .post("http://127.0.0.1:8000/documents/", formData)
      .then((res) => {
        alert("File Upload success");
        console.log(res);
      })
      .catch((err) => console.log(err.response));
  }

  return (
    <>
      <MDBModal
        show={props.basicModal}
        setShow={props.setBasicModal}
        tabIndex="-1"
      >
        <MDBModalDialog size="lg">
          <MDBModalContent>
            <MDBModalHeader>
              <MDBModalTitle>Dokument hinzufügen</MDBModalTitle>
              <MDBBtn
                className="btn-close"
                color="none"
                onClick={props.toggleShow}
              ></MDBBtn>
            </MDBModalHeader>
            <MDBModalBody>
              <MDBTabs pills fill className="mb-3">
                <MDBTabsItem>
                  <MDBTabsLink
                    onClick={() => handleFillClick("tab1")}
                    active={fillActive === "tab1"}
                  >
                    Rechnung
                  </MDBTabsLink>
                </MDBTabsItem>
                <MDBTabsItem>
                  <MDBTabsLink
                    onClick={() => handleFillClick("tab2")}
                    active={fillActive === "tab2"}
                  >
                    Sonstige
                  </MDBTabsLink>
                </MDBTabsItem>
              </MDBTabs>

              <MDBTabsContent>
                <MDBTabsPane show={fillActive === "tab1"}>
                  <InvoiceForm />
                </MDBTabsPane>
                <MDBTabsPane show={fillActive === "tab2"}>
                  <OtherDocumentsForm />
                </MDBTabsPane>
              </MDBTabsContent>
            </MDBModalBody>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  );
}

export default DocumentsModal;
