import React, { useEffect, useState } from "react";
import { MDBRow, MDBCol } from "mdb-react-ui-kit";
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

function AnmeldungDokumente(props) {

  const handleChangeMultiple = (event) => {
    const { options } = event.target;
    const value = [];

    for (let i = 0, l = options.length; i < l; i += 1) {
        if (options[i].selected) {
            value.push(options[i].value.id);
        }
        }
        props.setUsedDocuments(value);
    };


  return (
    <>
      <h5 className="mb-4">Weitere Dokumente</h5>


            <FormControl sx={{ m: 2, minWidth: 200, maxWidth: 500 }}>
                <InputLabel shrink htmlFor="select-multiple-native">
                Verwendete Dokumente
                </InputLabel>
                <Select
                multiple
                native
                value={props.documents}
                // @ts-ignore Typings are not considering `native`
                onChange={handleChangeMultiple}
                label="Native"
                inputProps={{
                    id: 'select-multiple-native',
                }}
                >
                {props.documents.map((document) => (
                    <option key={document.id} value={document}>
                      {document.representation}
                    </option>
                ))}
                </Select>
            </FormControl>
    </>
  );
}

export default AnmeldungDokumente;
