import React, { useEffect, useState } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBBtn,
  MDBSwitch,
} from "mdb-react-ui-kit";
import axiosInstance from "../../axiosApi";
import Autocomplete from '@mui/material/Autocomplete';
import TextField from '@mui/material/TextField';
import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';


function TransactionForm() {

  const [importeur, setImporteur] = useState("");  
  const [checkImporteur, setCheckImporteur] = useState(false);  
  const [description, setDescription] = useState("");
  const [partnership, setPartnership] = useState("")
  const [partners, setPartners] = useState([])


  useEffect(() => {
    //lookup partner for checkbox
    axiosInstance.get('partnership/').then((res) => {
      console.log(res.data);
      setPartners(res.data);
    });

  }, []);

  function handleSubmit(e) {
    let importeur = 349394;
    e.preventDefault();
    const newTransaction = {
      description,
      partnership,
      importeur
    }
    console.log(newTransaction)
    //firma hinzufügen 
    axiosInstance
      .post('/transactions/', newTransaction)
      .then(alert("neue Transaktion angelegt"))
      .catch(error => {
          this.setState({ errorMessage: error.message });
          console.error('There was an error!', error);
      });
  };

  return (
    <>
    <form onSubmit={handleSubmit}>
      <MDBContainer>
        <MDBRow>
          <Autocomplete
              disablePortal
              id="combo-box-partner"
              options={partners}
              //choose name as label for ui
              getOptionLabel={option => option.partner.name}
              //when changed, save choosen Partner
              onChange={(event, value) => setPartnership(value.id)}
              sx={{ width: 300 }}
              renderInput={(params) => <TextField {...params} label="Partner" />}
          />
          <br/>
        </MDBRow>
        <MDBRow>
          <MDBInput
            className="mb-3"
            name="description"
            label="Beschreibung der Transaktion"
            type="text"
            onChange={e => setDescription(e.target.value)}
          />
        </MDBRow>
        <FormControlLabel control={
          <Checkbox
            onClick={() => setCheckImporteur(!checkImporteur)}
          />

          } label="Ist ihre Firma der Importeur?" />
        <MDBRow>
          <MDBBtn type="submit" color="success">Hinzufügen</MDBBtn>
        </MDBRow>      

      </MDBContainer>
    </form>
    </>
  );
}

export default TransactionForm;
