import React, { Component, useEffect, useState} from "react";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardSubTitle,
  MDBCardText,
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBIcon,
  MDBCardFooter,
} from "mdb-react-ui-kit";
import { Link } from "react-router-dom";
import TransactionSpeedDial from "./TransactionSpeedDial";
import useAuth from "../../hooks/useAuth";

function Transaction(props) {

  const { auth } = useAuth();
  const [isImporteur, setIsImporteur] = useState(false);

  useEffect(() => {
    if (auth.eori_nr == props.importeur) {
      setIsImporteur(true);
    }
  })


  return (
    <div>
      <MDBCard>
        <MDBCardBody>
          <MDBContainer>
            <MDBCardTitle>{props.title}</MDBCardTitle>
            <TransactionSpeedDial id={props.id} title={props.title} />

            <MDBCardSubTitle>Hinzugef√ºgt am: {props.added}</MDBCardSubTitle>
            <br />
          </MDBContainer>
          <MDBContainer>
            <MDBRow>
              <MDBCol sm="2">
                <MDBIcon fas icon="clipboard-list" size="4x" className="mt-3" />
              </MDBCol>
              <MDBCol sm="5">
                <MDBCardText className="mb-3">
                  <b>Handelspartner</b>
                  {isImporteur && "Exporteur"}
                  <br />
                  {props.exportAddress}
                  <br />
                  {props.exportStreet}
                  <br />
                  {props.exportCity}, {props.exportCountry}
                </MDBCardText>
              </MDBCol>
              <MDBCol sm="5">
                <MDBCardText className="mb-2">
                  <b>Anzahl Dokumente: </b>3 (STATISCH)
                  <br />
                  <b>Rechnungsdokument: </b>liegt vor (STATISCH)
                  <br />
                  <b>Zollanmeldung: </b>ausstehend (STATISCH)
                  <br />
                  <b>Zollbescheid: </b>ausstehend (STATISCH)
                </MDBCardText>
              </MDBCol>
            </MDBRow>
            <br />
            <MDBRow>
              <MDBCol md="8" lg="8">
                <div className="alert alert-warning" role="alert">
                  Status: {props.status}
                </div>
              </MDBCol>
              <MDBCol md="4" lg="4">
                <Link
                  style={{ display: "block", margin: "1rem 0" }}
                  to={props.id.toString()}
                  key={props.id}
                  params={{ title: "Hello" }}
                >
                  <MDBBtn className="float-end">Dokumente anzeigen</MDBBtn>
                </Link>
              </MDBCol>
            </MDBRow>
          </MDBContainer>
          <MDBCardFooter>
            Zuletzt aktualisiert: {props.lastChange}
          </MDBCardFooter>
        </MDBCardBody>
      </MDBCard>
    </div>
  );
}

export default Transaction;
