import React from "react";
import { MDBContainer, MDBCol, MDBRow } from "mdb-react-ui-kit";
import Partner from "../components/Partners/Partner";
import PartnersModal from "../components/Partners/PartnersModal";
import axiosInstance from "../axiosApi";
import PartnerRequests from "../components/Partners/PartnerRequests";
import CompanyModal from "../components/Partners/CompanyModal";
import { Form } from "react-bootstrap";

class Partners extends React.Component {
  constructor(props) {
    super(props);
    // Props werden von Ã¼bergeordneter Klasse geerbt, kÃ¶nnen aber nicht
    // verÃ¤ndert werden
    //
    // state wird verwendet um Zustand der Seite durch setState() aktualisieren
    // zu kÃ¶nnen
    this.state = {
      partners: [],
    };
  }

  componentDidMount() {
    axiosInstance
      .get(`http://tradechain-app.herokuapp.com/partnership/?is_archived=false`)
      .then((res) => {
        console.log(res.data);
        const partners = res.data;
        this.setState({ partners });
      });
  }

  render() {
    return (
      <div>
        <MDBContainer>
          <MDBRow>
            <MDBCol>
              <h3>Handelsbeziehungen</h3>
            </MDBCol>

            <MDBCol>
              <CompanyModal />
            </MDBCol>
          </MDBRow>
          <br />
          <PartnerRequests />

          <nav className="my-3 navbar navbar-expand-lg navbar-light bg-light">
            <MDBContainer>
              <Form.Select
                style={{
                  minWidth: 250,
                  maxWidth: 250,
                }}
              >
                <option value={"UpdatedDesc"}>Zuletzt geÃ¤ndert ğŸ —</option>
                <option value={"UpdatedAsc"}>
                  Zuletzt geÃ¤ndert aufsteigend ğŸ •
                </option>
                <option value={"DescriptionDesc"}>
                  Beschreibung absteigend ğŸ —
                </option>
                <option value={"DescriptionAsc"}>
                  Beschreibung aufsteigend ğŸ •
                </option>
                <option value={"AddedDesc"}>HinzugefÃ¼gt am absteigend ğŸ —</option>
                <option value={"AddedAsc"}>HinzugefÃ¼gt am aufsteigend ğŸ •</option>
              </Form.Select>{" "}
              <div className="float-left">
                {this.state.partners.length} Partner
              </div>
              <PartnersModal 
                partnerships = {this.partners }
              />
              {/* <TransactionModal setreload={setreload} reload={reload} /> */}
            </MDBContainer>
          </nav>
          {this.state.partners.map((partner) => (
            <React.Fragment>
              <Partner
                key={partner.partner.eori_nr}
                id={partner.partner.eori_nr}
                id_relation={partner.id}
                title={partner.partner.name}
                added={partner.date_added}
                addressName="Bedeutung des Feldes?"
                addressStreet={partner.partner.street}
                addressCity={partner.partner.city}
                addressCountry={partner.partner.country_code}
                email={partner.partner.email}
                phone={partner.partner.phone}
                eori={partner.partner.eori_nr}
                onDelete={this.handleDelete}
              />
              <br />
            </React.Fragment>
          ))}
        </MDBContainer>
      </div>
    );
  }
  // send delete to database
  // Delete Funktion
  handleDelete = (partnerId) => {
    const partners = this.state.partners.filter((e) => e.id !== partnerId);
    this.setState({ partners });
  };
}

export default Partners;
