import axiosInstance from "../axiosApi"
import useAuth from "./useAuth"


const useRefreshToken = () => {
    const { setAuth } = useAuth();
    const refreshToken = localStorage.getItem("refresh_token");
    console.log(`Neuer Access token wird angefordert. Genutzter Refresh Token${refreshToken}`);
    const refresh = async() => {
        console.log("refresh funktion ausgef端hrt");
        const response = await axiosInstance.post('/api/token/refresh/', 
            //Refresh Token an Server f端r neuen Access Token
            JSON.stringify({refresh: refreshToken}),
        );
        setAuth(prev => {
            console.log(JSON.stringify(prev));
            console.log(`New Acess received token:${response.data.access}`);
            return {
                ...prev,
                roles: response.data.roles,
                accessToken: response.data.access
            }
        });
        return response.data.access; 
    }
  return refresh

}

export default useRefreshToken
