  // State für "Dokument anzeigen" Modal
  const [showDocumentModal, setShowDocumentModal] = useState(false);
  // Funktion für Manipulation von Modal (als props weitergereicht)
  function toggleShowDocumentModal() {
    setShowDocumentModal(!showDocumentModal);
  }

  return (
    <>
      <MDBCard className="h-100" style={{ minHeight: "300px", width: "300px" }}>
        <MDBCardBody>
          <MDBCardTitle>
            {props.type + " " + "(" + props.documentDate + ")"}
          </MDBCardTitle>
          <MDBCardText className="my-4">
            {props.description == "" ? <br /> : props.description}
          </MDBCardText>

          {/* {props.confirmed ? (
              <span> Bestätigt</span>
            ) : (
              <span> warten auf bestätigung</span>
            )}
          </MDBCardText>
          <MDBBtn outline onClick={toggleShowDocumentModal}>
            Anzeigen
          </MDBBtn>
          {ShowConfirmation ? (
            <MDBBtn outline onClick={confirmDocument}>
              Freigeben
            </MDBBtn>
          ) : (
            <br />
