import React, { useEffect, useState } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBBtn,
} from "mdb-react-ui-kit";
import axiosInstance from "../../axiosApi";
import Autocomplete from '@mui/material/Autocomplete';
import TextField from '@mui/material/TextField';


function TransactionForm() {

  const [eori_nr, setEoriNr] = useState("");
  const [importeur, setImporteur] = useState("");
  const [description, setDescription] = useState("");
  const [partner, setPartner] = useState("")
  const [partners, setPartners] = useState([])
  const top100Films = [
    { label: 'The Godfather', id: 1 },
    { label: 'Pulp Fiction', id: 2 },
  ];

/*   function onTagsChange (event, values) {
    setPartner({
      tags: values
    }, () => {
      // This will output an array of objects
      // given by Autocompelte options property.
      console.log(partner);
    });
  } */

  useEffect(() => {
    axiosInstance.get('partnership/').then((res) => {
      console.log(res.data);
      setPartners(res.data);
    });
  }, []);
  function handleSubmit(e) {
    e.preventDefault();
    const newTransaction = {
      eori_nr,
      importeur,
      description,
      partner,
    }
    console.log(newTransaction)
    //firma hinzufügen 
/*     axiosInstance
      .post('/companies/', newCompany)
      .then(console.log())
      .catch(error => {
          this.setState({ errorMessage: error.message });
          console.error('There was an error!', error);
      }).then(
        axiosInstance
        .post('/partnership/', {"added_partner":eori_nr})
        .then(console.log())
        .catch(error => {
            this.setState({ errorMessage: error.message });
            console.error('There was an error!', error);
        })); */
      //firma als partner hinterlegen
  };

  return (
    <>
    <form onSubmit={handleSubmit}>
      <MDBContainer>
        <MDBInput
          className="my-3"
          name="eori_nr"
          label="EORI-Nr. Partner"
          type="number"
          onChange={e => setEoriNr(e.target.value)}

        />
        <MDBInput
          className="mb-3"
          name="rolle"
          label="Importeur?"
          type="text"
          onChange={e => setImporteur(e.target.value)}
        />
        <MDBInput
          className="mb-3"
          name="description"
          label="Beschreibung der Transaktion"
          type="text"
          onChange={e => setDescription(e.target.value)}
        />
        <Autocomplete
            disablePortal
            id="combo-box-demo"
            options={partners}
            getOptionLabel={option => option.partner.name}
            onChange={(event, value) => setPartner(value.id)}
            sx={{ width: 300 }}
            renderInput={(params) => <TextField {...params} label="Movie" />}
        />

        <MDBBtn type="submit" color="success">Hinzufügen</MDBBtn>

      </MDBContainer>
    </form>
    </>
  );
}

export default TransactionForm;
