import React, { useState } from "react";
import "./App.css";

import SideNavigation from "./components/UI/SideNavigation";
import TopNavigation from "./components/UI/TopNavigation";

import Dashboard from "./pages/Dashboard";
import Transactions from "./pages/Transactions";
import Partners from "./pages/Partners";
import Settings from "./pages/Settings";
import NotFound from "./pages/NotFound";
import Login from "./pages/Login";
import DocumentsNew from "./pages/DocumentsNew";
import LoginNew from "./pages/login_new";

import Documents from "./pages/Documents";
import Declaration from "./pages/Declaration";
import Test from "./pages/Test";

import Footer from "./components/UI/Footer";

import RequireAuth from "./components/requireAuth";
import PersistLogin from "./components/PersistLogin";

import { BrowserRouter, Routes, Route } from "react-router-dom";

import ProtectedRoute from "./pages/ProtectedRoute";
import { AuthProvider } from "./context/AuthProvider";

// State der Sidebar: eingeklappt oder ausgeklappt
function App() {
  const [expandedNav, setExpandedNav] = useState(false);

  return (
    <React.Fragment>
        {/* Anpassung der TopNavigation Position an Sidebar State */}
        <div
          class="TopNavigation"
          style={{
            marginLeft: expandedNav ? 240 : 64,
            padding: "0px 0px 0px 0px",
            transition: "0.2s",
          }}
        >
          <TopNavigation />
        </div>
        <SideNavigation
          expandedNav={expandedNav}
          setExpandedNav={setExpandedNav}
        />
        {/* Anpassung de Content Position an Sidebar State 
        (muss alle Routes einschlie√üen) */}
        <div
          class="Content"
          style={{
            marginLeft: expandedNav ? 240 : 64,
            padding: "0px 20px 0px 20px",
            transition: "0.2s",
          }}
        >

  <Routes>

{/*public routes*/}
    <Route path="/login" element={<LoginNew />} />

    {/* private*/}
    <Route element = {<PersistLogin />}>
      <Route element = {<RequireAuth/>}>
          <Route path="/" element={<Dashboard />} />
          <Route path="/transactions" element={<Transactions />} />
          <Route
            path="/transactions/:transactionId"
            element={<DocumentsNew />}
          />
          <Route path="/partners" element={<Partners />} />
          <Route path="/settings" element={<Settings />} />
          <Route path="/login_new" element={<LoginNew />} />
          <Route path="/documentsnew" element={<DocumentsNew />} />

      </Route>
      <Route path="*" element={<NotFound />} />
    </Route>
</Routes>
        </div>
        <Footer />
    </React.Fragment>
  );
}

export default App;
