import React, { useState } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBBtn,
  MDBBadge,
} from "mdb-react-ui-kit";
import { init, createRechnung } from "../../Web3Client";

import axiosInstance from "../../axiosApi";

var crypto = require ('crypto');

function InvoiceForm() {
  const [invoiceFields, setInvoiceFields] = useState({
    invoiceDate: "",
    invoiceCurrency: "",
    invoiceDescription: "",
  });

  const [invoiceItemFields, setInvoiceItemFields] = useState([
    {
      invoiceItemName: "",
      invoiceItemQuantity: "",
      invoiceItemUnit: "",
      invoiceItemValue: "",
    },
  ]);

  function handleInvoiceChange(event) {
    const currentInvoiceFields = {
      invoiceDate: invoiceFields.invoiceDate,
      invoiceCurrency: invoiceFields.invoiceCurrency,
      invoiceDescription: invoiceFields.invoiceDescription,
    };

    const target = event.target;
    const value = target.value;
    const name = target.name;

    currentInvoiceFields[name] = value;

    setInvoiceFields(currentInvoiceFields);
  }

  function handleItemChange(index, event) {
    const values = [...invoiceItemFields];
    values[index][event.target.name] = event.target.value;
    setInvoiceItemFields(values);
  }

  function handleSubmit(e) {
    e.preventDefault();


    console.log(invoice)
    //console.log("invoiceFields", invoiceFields);
    //console.log("invoiceItemFields", invoiceItemFields);
    console.log(JSON.stringify(invoiceFields));
    console.log(JSON.stringify(invoiceItemFields));
    //Blockchain Transaktion
    createRechnung("2 Mark 50!", "0x8d242e4bc081e2eeD5eb9d6BF734DdF5d2F435e0", invoiceFields.invoiceCurrency, invoiceFields.invoiceDate, crypto.createHash('sha256').update(JSON.stringify(invoiceItemFields)).digest('hex'))
      .then((tx) => {
        console.log("Transaktion erfolgreich: " + tx + "  " + crypto.createHash('sha256').update(JSON.stringify(invoiceItemFields)).digest('hex'));
      })
      .catch((err) => {
        console.log("Transaktion fehlgeschlagen: "+ "  " + err +crypto.createHash('sha256').update(JSON.stringify(invoiceItemFields)).digest('hex'));
      });

    const invoice = {
      type:"Rechnung",
      description:invoiceFields.invoiceDescription,
      blockchainID:"232323",
      issue_date:invoiceFields.invoiceDate,
      invoice_text:invoiceFields.invoiceDescription,
      currency:invoiceFields.invoiceCurrency,
      transaction:3,
      owner:2      
    }
    //Übermittel und anlegen der Rechnung
    axiosInstance
      .post('/invoice/', invoice)
      .then((res) => {
        console.log(res.data)
      })
      .catch(error => {
          console.error('There was an error!', error);
      });
  }

  function handleAddFields() {
    setInvoiceItemFields([
      ...invoiceItemFields,
      {
        invoiceItemName: "",
        invoiceItemQuantity: "",
        invoiceItemUnit: "",
        invoiceItemValue: "",
      },
    ]);
  }

  function handleRemoveFields(index) {
    const values = [...invoiceItemFields];
    // values.splice(index, 1);
    values.splice(
      values.findIndex((value) => value.index === index),
      1
    );
    setInvoiceItemFields(values);
  }

  return (
    <MDBContainer>
      <MDBRow>
        <MDBCol>
          <MDBInput
            className="mb-4"
            name="invoiceDate"
            id="invoiceDate"
            label="Rechnungsdatum"
            type="date"
            onChange={(event) => handleInvoiceChange(event)}
          />
        </MDBCol>
        <MDBCol>
          <MDBInput
            className="mb-4"
            name="invoiceCurrency"
            id="invoiceCurrency"
            label="Währung"
            type="text"
            onChange={(event) => handleInvoiceChange(event)}
          />
        </MDBCol>
      </MDBRow>
      <MDBInput
        wrapperClass="mb-4"
        textarea
        name="invoiceDescription"
        id="invoiceDescription"
        rows={3}
        label="Beschreibung (optional)"
        onChange={(event) => handleInvoiceChange(event)}
      />
      <hr />

      <form onSubmit={handleSubmit}>
        {invoiceItemFields.map((inputField, index) => (
          <div key={index}>
            <h6>Rechnungsposten {index + 1}</h6>
            <MDBInput
              className="mb-4"
              name="invoiceItemName"
              label="Produktname"
              type="text"
              value={inputField.invoiceItemName}
              onChange={(event) => handleItemChange(index, event)}
            />
            <MDBRow>
              <MDBCol>
                <MDBInput
                  className="mb-4"
                  name="invoiceItemQuantity"
                  label="Anzahl"
                  type="number"
                  value={inputField.invoiceItemQuantity}
                  onChange={(event) => handleItemChange(index, event)}
                />
              </MDBCol>
              <MDBCol>
                <MDBInput
                  className="mb-4"
                  name="invoiceItemUnit"
                  label="Einheit"
                  type="text"
                  value={inputField.invoiceItemUnit}
                  onChange={(event) => handleItemChange(index, event)}
                />
              </MDBCol>
              <MDBCol>
                <MDBInput
                  className="mb-4"
                  name="invoiceItemValue"
                  label="Preis pro Enheit"
                  type="number"
                  value={inputField.invoiceItemValue}
                  onChange={(event) => handleItemChange(index, event)}
                />
              </MDBCol>
              <MDBCol>
                <MDBBadge className="mt-2 me-4 float-end" color="primary">
                  Betrag :{" "}
                  {inputField.invoiceItemQuantity * inputField.invoiceItemValue}{" "}
                  {invoiceFields.invoiceCurrency}
                </MDBBadge>
              </MDBCol>
            </MDBRow>
          </div>
        ))}
        <br />

        <MDBBtn className="me-3 mb-3" color="success" onClick={handleAddFields}>
          Rechnungsposten hinzufügen
        </MDBBtn>
        {invoiceItemFields.length > 1 && (
          <MDBBtn
            className="me-3 mb-3"
            color="danger"
            onClick={handleRemoveFields}
          >
            Rechnungsposten entfernen
          </MDBBtn>
        )}

        <MDBBtn className="float-end" color="primary" onClick={handleSubmit}>
          Abschicken
        </MDBBtn>
      </form>
    </MDBContainer>
  );
}

export default InvoiceForm;
