import React, { useEffect, useState } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBBtn,
  MDBSwitch,
  MDBIcon,
} from "mdb-react-ui-kit";
import axiosInstance from "../../axiosApi";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import Checkbox from "@mui/material/Checkbox";
import FormControlLabel from "@mui/material/FormControlLabel";
import { Link } from "react-router-dom";

function TransactionForm() {
  const [importeur, setImporteur] = useState("");
  const [checkImporteur, setCheckImporteur] = useState(false);
  const [description, setDescription] = useState("");
  const [partnership, setPartnership] = useState("");
  const [partners, setPartners] = useState([]);
  const [userCompany, setUserCompany] = useState();
  const [partnerCompany, setPartnerCompany] = useState();

  useEffect(() => {
    //lookup partner for checkbox
    axiosInstance.get("partnership/").then((res) => {
      console.log(res.data);
      setPartners(res.data);
    });
    //save current user company id for choosing importeur
    axiosInstance.get("employee/").then((res) => {
      let id = res.data[0].company;
      setUserCompany(id);
    });
  }, []);

  function handleSubmit(e) {
    console.log(userCompany);
    console.log(partnerCompany);
    let importeur = 1;
    if (checkImporteur) {
      importeur = userCompany;
      console.log("importeur ist eigene Firma");
    } else {
      importeur = partnerCompany;
    }
    console.log("Die Importfirma ist", importeur);
    e.preventDefault();
    const newTransaction = {
      description,
      partnership,
      importeur,
    };
    console.log(newTransaction);
    //add transaction
    axiosInstance
      .post("/transactions/", newTransaction)
      .then((res) => {
        console.log(res.data);
      })
      .catch((error) => {
        console.error("There was an error!", error);
      });
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <MDBContainer>
          <MDBRow>
            <MDBInput
              name="description"
              label="Beschreibung der Transaktion"
              type="text"
              onChange={(e) => setDescription(e.target.value)}
            />
          </MDBRow>
          <div className="mt-4">Partner auswählen:</div>
          <MDBRow>
            <MDBCol>
              <Autocomplete
                className="mt-1"
                disablePortal
                id="combo-box-partner"
                options={partners}
                //choose name as label for ui
                getOptionLabel={(option) => option.partner.name}
                //when changed, save choosen Partner
                onChange={(event, value) => {
                  setPartnership(value.id);
                  setPartnerCompany(value.partner.eori_nr);
                }}
                sx={{ width: 320 }}
                renderInput={(params) => (
                  <TextField {...params} label="Partner" />
                )}
              />
            </MDBCol>
            <MDBCol>
              <Link to="/partners">
                <MDBIcon
                  far
                  icon="address-book"
                  className="float-end mt-3 me-4"
                  size="2x"
                />
              </Link>
            </MDBCol>
          </MDBRow>

          <MDBRow>
            <FormControlLabel
              className="mt-3"
              control={
                <Checkbox onClick={() => setCheckImporteur(!checkImporteur)} />
              }
              label="Ist ihre Firma der Importeur?"
            />
          </MDBRow>
          <div className="d-grid mx-auto">
            <MDBBtn type="submit" color="success" className="mt-4">
              Hinzufügen
            </MDBBtn>
          </div>
        </MDBContainer>
      </form>
    </>
  );
}

export default TransactionForm;
