import React, { useEffect, useState } from "react";
import {
  MDBAccordion,
  MDBAccordionItem,
  MDBTable,
  MDBTableHead,
  MDBTableBody,
  MDBBtn,
  MDBIcon,
} from "mdb-react-ui-kit";
import { Accordion } from "react-bootstrap";
import useAxiosPrivate from "../../hooks/useAxiosPrivate";

export default function PartnerRequests() {
  const axiosInstance = useAxiosPrivate()

  const [openRequests, setopenRequests] = useState()

  const [userCompanyEORI, setUserCompanyEORI] = useState()

  const [partnerRequests, setPartnerRequests] = useState([
    {
      request_id: 1,
      name: "Los Pollos Hermanos",
      eori_nr: 381290381903,
      date: "09.06.2022",
    },
  ]);

  useEffect(() => {
    setUserCompanyEORI(349394)
  }, [])

  useEffect(() => {
    axiosInstance.get(`partnership/?partner2=${userCompanyEORI}&confirmed=false`).then((res)=> {
      setopenRequests(res.data)
    });

  }, [userCompanyEORI])

  useEffect(() => {
    console.log(openRequests)

  }, [openRequests])



  // setPartnerRequests({
  //   name: "Maybach Wein Gmbh",
  //   eori_nr: 381290381903,
  //   date: "09.06.2022",
  // });

  function handleDelete() {}

  function handleAccept() {}

  return (
    <Accordion defaultActiveKey={partnerRequests.length !== 0 ? "0" : "1"}>
      <Accordion.Item eventKey="0">
        <Accordion.Header>
          Ausstehende Partneranfragen (<b>{partnerRequests.length}</b>)
        </Accordion.Header>
        <Accordion.Body>
          {partnerRequests.length == 0 ? (
            "Es liegen keine Anfragen vor."
          ) : (
            <MDBTable responsive className="align-middle">
              <MDBTableHead>
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">Unternehmen</th>
                  <th scope="col">EORI-Nr.</th>
                  <th scope="col">Anfragedatum</th>
                  <th scope="col"></th>
                </tr>
              </MDBTableHead>
              <MDBTableBody>
                {partnerRequests.map((partnerRequest, index) => (
                  <tr key={index}>
                    <th scope="row">
                      <div className="">{index + 1}</div>
                    </th>
                    <td>
                      <div className="">{partnerRequest.name}</div>
                    </td>
                    <td>
                      <div className="">{partnerRequest.eori_nr}</div>
                    </td>
                    <td>
                      <div className="">{partnerRequest.date}</div>
                    </td>
                    <td>
                      <div className="float-end">
                        <MDBBtn
                          color="success"
                          outline
                          rounded
                          onClick={() => handleAccept()}
                        >
                          <MDBIcon size="lg" fas icon="check" />
                        </MDBBtn>
                        <MDBBtn
                          color="danger mx-2"
                          className=""
                          outline
                          rounded
                          onClick={() => handleDelete()}
                        >
                          <MDBIcon size="lg" fas icon="times" />
                        </MDBBtn>
                      </div>
                    </td>
                  </tr>
                ))}
              </MDBTableBody>
            </MDBTable>
          )}
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
  );
}
