import React, { useEffect, useState } from "react";
import "./css/LoadingSpinner.css";
import {
  MDBContainer,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBBtn,
  MDBCardHeader,
  MDBRow,
  MDBCol,
  MDBIcon,
  MDBSpinner,
} from "mdb-react-ui-kit";
import Toast from "react-bootstrap/Toast";

import useAuth from "../hooks/useAuth";

import DashboardDocumentCard from "../components/Dashboard/DashboardDocumentCard";
import axiosInstance from "../axiosApi";
import { Backdrop } from "@mui/material";
import { CircularProgress } from "@mui/material";
import { Link } from "react-router-dom";
import NotificationToast from "../components/Dashboard/NotifcationToast";

function Dashboard() {
  // Transaktionen
  const [transactionsData, setTransactionsData] = useState([]);

  // Dokumente
  const [documentsData, setDocumentsData] = useState([]);

  const [openRequests, setopenRequests] = useState([]);

  const [isLoading, setIsLoading] = useState(true);

  const { auth } = useAuth();

  async function getDashboardData() {
    //Abfragen der Transaktionen
    await axiosInstance
      .get("transactions/")
      .then((res) => {
        setTransactionsData(res.data);
      })
      .catch((err) => {
        console.log(err);
      });

    //Abfragen der Dokumente
    await axiosInstance
      .get("documents/")
      .then((res) => {
        setDocumentsData(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
    //Abfragen der unbestÃ¤tigten Partnerschaften bei denen wir Partner 2 sind

    await axiosInstance
      .get(`partnership/?partner2=${auth.eori_nr}&confirmed=false`)
      .then((res) => {
        setopenRequests(res.data);
      })
      .catch(function (error) {
        if (error.response) {
          // The request was made and the server responded with a status code
          // that falls out of the range of 2xx
          console.log(error.response.data);
          console.log(error.response.status);
          console.log(error.response.headers);
        } else if (error.request) {
          // The request was made but no response was received
          // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
          // http.ClientRequest in node.js
          console.log(error.request);
        } else {
          // Something happened in setting up the request that triggered an Error
          console.log("Error", error.message);
        }
      });
    setIsLoading(false);
  }

  // Transaktionen abrufen
  useEffect(() => {
    getDashboardData();
  }, []);

  // Dokumente abrufen
  useEffect(() => {
    axiosInstance.get("documents/").then((res) => {
      setDocumentsData(res.data);
    });
  }, []);


  const [alerts, setAlerts] = useState([
    {
        "id": 34,
        "level": "info",
        "unread": true,
        "actor_object_id": "11",
        "verb": "wurde angelegt",
        "description": null,
        "target_object_id": null,
        "action_object_object_id": null,
        "timestamp": "2022-07-05T09:21:48.100353+02:00",
        "public": true,
        "deleted": false,
        "emailed": false,
        "data": null,
        "recipient": 8,
        "actor_content_type": 11,
        "target_content_type": null,
        "action_object_content_type": null
    },
    {
        "id": 33,
        "level": "info",
        "unread": true,
        "actor_object_id": "11",
        "verb": "wurde angelegt",
        "description": null,
        "target_object_id": null,
        "action_object_object_id": null,
        "timestamp": "2022-07-05T09:21:48.100353+02:00",
        "public": true,
        "deleted": false,
        "emailed": false,
        "data": null,
        "recipient": 4,
        "actor_content_type": 11,
        "target_content_type": null,
        "action_object_content_type": null
    },
    {
        "id": 32,
        "level": "info",
        "unread": true,
        "actor_object_id": "11",
        "verb": "wurde angelegt",
        "description": null,
        "target_object_id": null,
        "action_object_object_id": null,
        "timestamp": "2022-07-05T09:15:34.693338+02:00",
        "public": true,
        "deleted": false,
        "emailed": false,
        "data": null,
        "recipient": 8,
        "actor_content_type": 11,
        "target_content_type": null,
        "action_object_content_type": null
    }])

  return (
    <>
      {isLoading ? (
        <div className="loadingSpinner">
          <MDBSpinner>
            <span className="visually-hidden">LÃ¤dt..</span>
          </MDBSpinner>
        </div>
      ) : (
        <div>
          <MDBContainer>
            <h3>Dashboard</h3>
            <br />
            <MDBRow>
              <MDBCol className="col-md-6 mb-4">
                <MDBCard style={{ height: "500px" }}>
                  <MDBCardHeader style={{ background: "#fbfbfb" }}>
                    <b>Benachrichtigungen</b>
                  </MDBCardHeader>
                  <MDBCardBody className="overflow-scroll">
                    <MDBCardText>
                    {alerts.map((alert) => (
                      <NotificationToast
                        alert = {alert}
                      />
                    ))}


                    </MDBCardText>
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
              <MDBCol className="col-md-6 mb-4">
                <MDBCard style={{ height: "240px", marginBottom: "20px" }}>
                  <MDBCardHeader style={{ background: "#fbfbfb" }}>
                    <b>Anzahl aktiver AuftrÃ¤ge: </b> {transactionsData.length}
                  </MDBCardHeader>
                  <MDBCardBody>
                    {transactionsData !== [] ? (
                      <>
                        <i>Zuletzt aktualisiert:</i>
                        <MDBCardTitle>
                          {transactionsData[0].description}
                        </MDBCardTitle>
                        <MDBCardText>
                          {transactionsData[0].partnership.partner.name}
                          <br />
                          {transactionsData[0].partnership.partner.street}{" "}
                          <br />
                          {
                            transactionsData[0].partnership.partner.city_code
                          }{" "}
                          {transactionsData[0].partnership.partner.city},{" "}
                          {transactionsData[0].partnership.partner.country_code}{" "}
                          <Link
                            style={{}}
                            to={
                              "/transactions/" +
                              transactionsData[0].id.toString()
                            }
                            key={transactionsData[0].id}
                          >
                            <MDBBtn outline className="float-end">
                              Zum Auftrag
                            </MDBBtn>
                          </Link>
                        </MDBCardText>
                      </>
                    ) : (
                      <MDBCardTitle className="mt-5">
                        Keine neuen Anfragen
                      </MDBCardTitle>
                    )}
                  </MDBCardBody>
                </MDBCard>

                <MDBCard style={{ height: "240px" }}>
                  <MDBCardHeader style={{ background: "#fbfbfb" }}>
                    <b>Ausstehende Partneranfragen: </b> {openRequests.length}
                  </MDBCardHeader>
                  <MDBCardBody>
                    {openRequests.length != 0 ? (
