  }, []);

  useEffect(() => {



  }, []);



  // State für "Dokument anzeigen" Modal
  const [showDocumentModal, setShowDocumentModal] = useState(false);
  // Funktion für Manipulation von Modal (als props weitergereicht)
  function toggleShowDocumentModal() {
    setShowDocumentModal(!showDocumentModal);
  }

  return (
    <>
      <MDBCard className="h-100" style={{ maxWidth: "22rem" }}>
        <MDBCardHeader>Hochgeladen von: {props.author} - {props.author_company}</MDBCardHeader>
        <MDBCardBody>
          <MDBCardTitle>
            {props.type + " " + "(" + props.documentDate + ")"}
          </MDBCardTitle>
          <MDBCardText>{props.description}</MDBCardText>
          <MDBBtn outline onClick={toggleShowDocumentModal}>
            Anzeigen
          </MDBBtn>
      {!isOwner ? <MDBBtn outline onClick={toggleShowDocumentModal}>
            Freigeben
          </MDBBtn>
          : <span> test </span>
      }

          <MDBBtn outline onClick={toggleShowDocumentModal}>
            Löschen
          </MDBBtn>
        </MDBCardBody>
        <MDBCardFooter className="text-muted">
          Hochgeladen am: Test: {props.uploadDate}
        </MDBCardFooter>
      </MDBCard>
      <CardModal
        basicModal={showDocumentModal}
        setBasicModal={setShowDocumentModal}
        toggleShow={toggleShowDocumentModal}
        title={props.title}
        description={props.description}
      />
    </>
  );
}

export default DocumentCard;
