import React from "react";
import { MDBContainer, MDBCol, MDBRow } from "mdb-react-ui-kit";
import Partner from "../components/Partners/Partner";
import PartnersModal from "../components/Partners/PartnersModal";
import axiosInstance from "../axiosApi";

class Partners extends React.Component {
  constructor(props) {
    super(props);
    // Props werden von übergeordneter Klasse geerbt, können aber nicht
    // verändert werden
    //
    // state wird verwendet um Zustand der Seite durch setState() aktualisieren
    // zu können
    this.state = {
      partners: [],
    };
  }

  componentDidMount() {
    axiosInstance
      .get(`http://tradechain-app.herokuapp.com/partnership/`)
      .then((res) => {
        console.log(res.data);
        const partners = res.data;
        this.setState({ partners });
      });
  }

  render() {
    return (
      <div>
        <MDBContainer>
          <MDBRow>
            <MDBCol>
              <h3>Handelsbeziehungen</h3>
            </MDBCol>
{/*             <MDBCol>
              <PartnersModal />
            </MDBCol> */}
          </MDBRow>
          <br />
          {this.state.partners.map((partner) => (
            <React.Fragment>
              <Partner
                key={partner.partner.eori_nr}
                id={partner.partner.eori_nr}
                title={partner.partner.name}
                added={partner.date_added}
                addressName="Bedeutung des Feldes?"
                addressStreet={partner.partner.street}
                addressCity={partner.partner.city}
                addressCountry={partner.partner.country_code}
                email={partner.partner.email}
                phone={partner.partner.phone}
                eori={partner.partner.eori_nr}
                onDelete={this.handleDelete}
              />
              <br />
            </React.Fragment>
          ))}
        </MDBContainer>
      </div>
    );
  }
  // send delete to database
  // Delete Funktion
  handleDelete = (partnerId) => {
    const partners = this.state.partners.filter((e) => e.id !== partnerId);
    this.setState({ partners });
  };
}

export default Partners;
