import React, { useEffect, useState } from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardHeader,
  MDBTabs,
  MDBTabsItem,
  MDBTabsLink,
  MDBBtn,
  MDBRow,
  MDBCol,
  MDBTabsPane,
  MDBTabsContent,
  MDBIcon,
  MDBBreadcrumb,
  MDBBreadcrumbItem,
} from "mdb-react-ui-kit";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import axiosInstance from "../../axiosApi";

export default function PreviewDelacration() {

  const { transactionId } = useParams()
  const [customDeclaration, setCustomDeclaration] = useState({})

  useEffect( async () => {
    await axiosInstance.get("/declaration_auth/?transaction=" + transactionId)
    .then((res) => {
      console.log(res.data)
      setCustomDeclaration(res.data)
    })
  },[] )
  // -- DATENFELDER --

  // Auftrag Metadaten
  const [auftragMetadaten, setAuftragMetadaten] = React.useState({
    documentId: 23,
    transactionId: 18,
    documentType: "Zollanmeldung",
    dateAdded: "19.06.2022",
    author: "LG Electronics",
    freigabeStatus: true,
    description: "",
  });

  // State des Inhalts der Felder
/*   const [fieldValue, setFieldValue] = React.useState({
    // Allgemeine Angaben
    auftragsnummer: customDeclaration[0]?.transaction?.id,
    bearbeitendeDienststelle: customDeclaration[0]?.customs_office?.name,
    anmeldungArt: customDeclaration[0]?.anmeldeArt,
    geschaeftArt: customDeclaration[0]?.geschaeftArt,
    zahlungArt: customDeclaration[0]?.zahlungsArt,
    // Adressdaten Versender
    eoriVersender: customDeclaration[0]?.exporteur.eori_nr,
    // nameVersender: "",
    // vornameVersender: "",
    firmaVersender: customDeclaration[0]?.exporteur.name,
    strasseVersender: customDeclaration[0]?.exporteur.street,
    plzVersender: customDeclaration[0]?.exporteur.city_code,
    ortVersender: customDeclaration[0]?.exporteur.city,
    staatVersender: customDeclaration[0]?.exporteur.country_code,
    telefonVersender: customDeclaration[0]?.exporteur.phone,
    emailVersender: customDeclaration[0]?.exporteur.email,
    // Adressdaten Anmelder
    eoriAnmelder: customDeclaration[0]?.importeur.eori_nr,
    // nameAnmelder: "",
    // vornameAnmelder: "",
    firmaAnmelder: customDeclaration[0]?.importeur.name,
    strasseAnmelder: customDeclaration[0]?.importeur.street,
    plzAnmelder: customDeclaration[0]?.importeur.city_code,
    ortAnmelder: customDeclaration[0]?.importeur.city,
    staatAnmelder: customDeclaration[0]?.importeur.country_code,
    telefonAnmelder: customDeclaration[0]?.importeur.phone,
    emailAnmelder: customDeclaration[0]?.importeur.email,
    // Lieferdaten
    ausfuhrland: customDeclaration[0]?.ausfuhrland,
    bestimmungsland: customDeclaration[0]?.bestimmungsland,
    //bestimmungsbundesland: "Niedersachsen",
    befoerderungsmittel: customDeclaration[0]?.befoerderungsmittel,
    lieferbedingung: customDeclaration[0]?.lieferbedingung,
    lieferort: customDeclaration[0]?.lieferort,
    warenort: customDeclaration[0]?.importeur.warenort,

    //Rechnungsdaten
    datumRechnung: customDeclaration[0]?.invoice.issue_date,
    gesamtbetrag: 3500,
    waehrung: customDeclaration[0]?.invoice.currency,
    lieferkosten: customDeclaration[0]?.invoice.transport_costs,
  }); */

  const [rechnungsposten, setRechnungsposten] = React.useState([customDeclaration[0]?.invoiceItems]);

  // -- Tab-Steuerung --
  // State Ã¼ber aktiven Tab
  const [fillActive, setFillActive] = useState("tab1");

  // Funktion zum wechseln des Tabs
  function handleFillClick(value) {
    if (value === fillActive) {
      return;
    }

    setFillActive(value);
  }

  return (
    <>
      <h3>Dokument: Zollanmeldung</h3>
      <MDBBreadcrumb>
        <MDBBreadcrumbItem>
          <Link to="/transactions">AuftrÃ¤ge</Link>
        </MDBBreadcrumbItem>
        <MDBBreadcrumbItem>
          <Link to={"/transactions/" + auftragMetadaten.transactionId}>
            Auftrags-ID 18
          </Link>
        </MDBBreadcrumbItem>
        <MDBBreadcrumbItem active>
          Dokumenten-ID {auftragMetadaten.documentId}
        </MDBBreadcrumbItem>
      </MDBBreadcrumb>
      <MDBCard style={{ minHeight: 600 }}>
        <MDBCardHeader>
          <MDBTabs pills className="card-header-tabs">
            <MDBTabsItem>
              <MDBTabsLink
                onClick={() => handleFillClick("tab1")}
                active={fillActive === "tab1"}
              >
                Allgemeine Angaben
              </MDBTabsLink>
            </MDBTabsItem>
            <MDBTabsItem>
              <MDBTabsLink
                onClick={() => handleFillClick("tab2")}
                active={fillActive === "tab2"}
              >
                Adressdaten
              </MDBTabsLink>
            </MDBTabsItem>
            <MDBTabsItem>
              <MDBTabsLink
                onClick={() => handleFillClick("tab3")}
                active={fillActive === "tab3"}
              >
                Lieferdaten
              </MDBTabsLink>
            </MDBTabsItem>
            <MDBTabsItem>
              <MDBTabsLink
                onClick={() => handleFillClick("tab4")}
                active={fillActive === "tab4"}
              >
                Rechnungsdaten
              </MDBTabsLink>
            </MDBTabsItem>
          </MDBTabs>
        </MDBCardHeader>
        <MDBCardBody>
          <MDBRow>
            <MDBCol className="col-md-8">
              {fillActive === "tab1" ? (
                <MDBCardTitle className="mb-4">Allgemeine Angaben</MDBCardTitle>
              ) : fillActive === "tab2" ? (
                <MDBCardTitle className="mb-4">Adressdaten</MDBCardTitle>
              ) : fillActive === "tab3" ? (
                <MDBCardTitle className="mb-4">Lieferdaten</MDBCardTitle>
              ) : (
                <MDBCardTitle className="mb-4">Rechnungsdaten</MDBCardTitle>
              )}
              <MDBCardText>
                <MDBTabsContent>
                  <MDBTabsPane show={fillActive === "tab1"}>
                    <p>
                      <b>Auftragsnummer: </b>
                      {fieldValue.auftragsnummer}
                    </p>
                    <p>
                      <b>Bearbeitende Dienststelle: </b>
                      {fieldValue.bearbeitendeDienststelle}
                    </p>
                    <p>
                      <b>Art der Anmeldung: </b>
                      {fieldValue.anmeldungArt}
                    </p>
                    <p>
                      <b>Art des GeschÃ¤fts: </b>
                      {fieldValue.geschaeftArt}
                    </p>
                    <p>
                      <b>Zahlungsart: </b>
                      {fieldValue.zahlungArt}
                    </p>
                  </MDBTabsPane>
                  <MDBTabsPane show={fillActive === "tab2"}>
                    {/* Versender Daten */}
                    <h6>
                      <u>Versender</u>
                    </h6>
                    <p>
                      <b>EORI-Nummer: </b>
                      {fieldValue.eoriVersender}
                    </p>
                    <p>
                      <b>Firma: </b>
                      {fieldValue.firmaVersender}
                    </p>
                    <p>
                      <b>StraÃe u. Hausnummer: </b>
                      {fieldValue.strasseVersender}
                    </p>
                    <p>
                      <b>Postleitzahl: </b>
                      {fieldValue.plzVersender}
                    </p>
                    <p>
                      <b>Postleitzahl: </b>
                      {fieldValue.ortVersender}
                    </p>
                    <p>
                      <b>StaatsangehÃ¶rigkeit: </b>
                      {fieldValue.staatVersender}
                    </p>
                    <p>
                      <b>Telefonnummer: </b>
                      {fieldValue.telefonVersender}
                    </p>
                    <p>
                      <b>Telefonnummer: </b>
                      {fieldValue.emailVersender}
                    </p>
                    <br />
                    {/* Anmelder Daten */}
                    <h6>
                      <u>EmpfÃ¤nger / Anmelder</u>
                    </h6>
                    <p>
                      <b>EORI-Nummer: </b>
                      {fieldValue.eoriAnmelder}
                    </p>
                    <p>
                      <b>Firma: </b>
                      {fieldValue.firmaAnmelder}
                    </p>
                    <p>
                      <b>StraÃe u. Hausnummer: </b>
                      {fieldValue.strasseAnmelder}
                    </p>
                    <p>
                      <b>Postleitzahl: </b>
                      {fieldValue.plzAnmelder}
                    </p>
                    <p>
                      <b>Postleitzahl: </b>
                      {fieldValue.ortAnmelder}
                    </p>
                    <p>
                      <b>StaatsangehÃ¶rigkeit: </b>
                      {fieldValue.staatAnmelder}
                    </p>
                    <p>
                      <b>Telefonnummer: </b>
                      {fieldValue.telefonAnmelder}
                    </p>
                    <p>
                      <b>Telefonnummer: </b>
                      {fieldValue.emailAnmelder}
                    </p>
                  </MDBTabsPane>
                  <MDBTabsPane show={fillActive === "tab3"}>
                    <p>
                      <b>Ausfuhrland: </b>
                      {fieldValue.ausfuhrland}
                    </p>
                    <p>
                      <b>Bestimmungsland: </b>
                      {fieldValue.bestimmungsland}
                    </p>
                    <p>
                      <b>Bestimmungsbundesland: </b>
                      {fieldValue.bestimmungsbundesland}
                    </p>
                    <p>
                      <b>Art des grenzÃ¼berschreitenden BefÃ¶rderungsmittels: </b>
                      {fieldValue.befoerderungsmittel}
                    </p>
                    <p>
                      <b>Lieferbedingungen: </b>
                      {fieldValue.lieferbedingung}
                    </p>
                    <p>
                      <b>Lieferort: </b>
                      {fieldValue.lieferort}
                    </p>
                    <p>
                      <b>Lieferort: </b>
                      {fieldValue.warenort}
                    </p>
                  </MDBTabsPane>
                  <MDBTabsPane show={fillActive === "tab4"}>
                    <p>
                      <b>Rechnungsdatum: </b>
                      {fieldValue.datumRechnung}
                    </p>
                    <p>
                      <b>Gesamtbetrag: </b>
                      {fieldValue.gesamtbetrag} {fieldValue.waehrung}
                    </p>
                    <p>
                      <b>Lieferkosten: </b>
                      {fieldValue.lieferkosten} {fieldValue.waehrung}
                    </p>
                    <hr className="my-4" />
                    {rechnungsposten.map(
                        (posten) => (
                    <>
                    <hr className="my-4" />
                    <h6>
                      <u>Rechnungsposten 1</u>
                    </h6>
                    <p>
                      <b>Produkt: </b>
                      {posten.product}
                    </p>
                    <p>
                      <b>Anzahl: </b>
                      {posten.amount}
                    </p>
                    <p>
                      <b>Einheit: </b>
                      {posten.unit}
                    </p>
                    <p>
                      <b>Preis pro Einheit: </b>
                      {posten.price.toFixed(
                        2
                      )}{" "}
                      {fieldValue.waehrung}
                    </p>
                    </>
                  )
                )}
                    <hr className="my-4" />

                    {/* Berechnung Zollabgabe */}
                    <p className="ms-4">
                      <b>Zollwert</b> (Geamtbetrag + Lieferkosten) ={" "}
