import React, { useState, useEffect } from "react";
import axiosInstance from "../axiosApi";
import useAuth from "../hooks/useAuth";
import {
  MDBRow,
  MDBCol,
  MDBInput,
  MDBCheckbox,
  MDBBtn,
  MDBIcon,
  MDBTabs,
  MDBTabsItem,
  MDBTabsLink,
  MDBTabsContent,
  MDBTabsPane,
} from "mdb-react-ui-kit";

import "./css/NotFound.css";
import UniLogo from "../Uni_Logo.png";

import { useNavigate, useLocation } from "react-router-dom";

const LOGIN_URL = "/api/token/";

export default function Test5() {
  // -- Login --
  const { setAuth } = useAuth();

  const navigate = useNavigate();
  const location = useLocation();
  const from = location.state?.from?.pathname || "/";

  const [user, setUser] = useState("");
  const [pwd, setPwd] = useState("");
  const [errMsg, setErrMsg] = useState("");
  const [sucess, setSucess] = useState(false);

  useEffect(() => {
    setErrMsg("");
  }, [user, pwd]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axiosInstance.post(
        LOGIN_URL,
        JSON.stringify({ username: user, password: pwd }),
        {
          headers: { "Content-Type": "application/json" },
        }
      );
      console.log(JSON.stringify(response?.data));
      const accessToken = response?.data?.access;
      const roles = response?.data?.roles;
      const company = response?.data?.company;

      //Speichern der Daten für künftige Anfragen

      localStorage.setItem("access_token", accessToken);
      localStorage.setItem("refresh_token", response?.data?.refresh);
      //speichern von Nutzernamen und Firma im lokalen Speicher
      localStorage.setItem("username", response?.data?.user);
      localStorage.setItem("company", company);
      setAuth({ user, pwd, roles, company, accessToken });
      setUser("");
      setPwd("");
      setSucess(true);
      navigate(from, { replace: true });
    } catch (err) {
      if (!err?.response) {
        setErrMsg("Der Server antwortet nicht");
      } else if (err.response?.status === 400) {
        setErrMsg("Bitte alle Felder befüllen");
      } else if (err.response?.status === 401) {
        setErrMsg("Falsche Login-Daten");
      } else {
        setErrMsg("Allgemeiner Fehler");
      }
    }
  };

  // -- Tab-Steuerung --
  // State über aktiven Tab
  const [fillActive, setFillActive] = useState("login");

  // Funktion zum wechseln des Tabs
  function handleFillClick(value) {
    if (value === fillActive) {
      return;
    }

    setFillActive(value);
  }

  return (
    <div style={{ width: "26rem" }} className="text-middle">
      <img src={UniLogo} style={{ width: "26rem" }} className="mb-5" />
      {sucess ? (
        <section>
          <h1> Du bist eingeloggt</h1>
          <br />
          <p>
            <a href="#">Zur Startseite</a>
          </p>
        </section>
      ) : (
        <section>
          <MDBTabs pills justify className="mb-3">
            <MDBTabsItem>
              <MDBTabsLink
                onClick={() => handleFillClick("login")}
                active={fillActive === "login"}
              >
                Login
              </MDBTabsLink>
            </MDBTabsItem>
            <MDBTabsItem>
              <MDBTabsLink
                onClick={() => handleFillClick("register_company")}
                active={
                  fillActive === "register_employee" ||
                  fillActive === "register_company"
                }
              >
                Registrieren
              </MDBTabsLink>
            </MDBTabsItem>
          </MDBTabs>

          <MDBTabsContent>
            <MDBTabsPane show={fillActive === "login"}>
              <form onSubmit={handleSubmit}>
                <br />
                <MDBInput
                  className="mb-4"
                  type="username"
                  id="username"
                  name="username"
                  label="Nutzername"
                  onChange={(e) => setUser(e.target.value)}
                />
                <MDBInput
                  className="mb-4"
                  type="password"
                  id="password"
                  name="password"
                  label="Passwort"
                  onChange={(e) => setPwd(e.target.value)}
                />

                <br />

                <MDBBtn type="submit" className="mb-4" block>
                  Einloggen
                </MDBBtn>

                <div className="text-center">
                  <p>
                    Noch nicht Mitglied?{" "}
                    <a
                      href="javascript:;"
                      onClick={() => handleFillClick("register_company")}
                    >
                      Registrieren
                    </a>
                  </p>
                </div>
              </form>
              <p
                className={errMsg ? "errmsg alert alert-danger" : "offscreen"}
                aria-live="assertive"
              >
                {errMsg}
              </p>
            </MDBTabsPane>
            <MDBTabsPane show={fillActive === "register_company"}>
              <form>
                <MDBInput
                  className="mb-4"
                  id="name_firma"
                  label="Name des Unternehmens"
                />
                <MDBInput
                  className="mb-4"
                  id="eori"
                  type="number"
                  label="EORI-Nr."
                />
                <MDBInput className="mb-4" id="strasse" label="Straße u. Nr." />
                <MDBRow>
                  <MDBCol className="col-4">
                    <MDBInput
                      className="mb-4"
                      type="number"
                      id="plz"
                      label="PLZ"
                    />
                  </MDBCol>
                  <MDBCol>
                    <MDBInput className="mb-4" id="stadt" label="Stadt" />
                  </MDBCol>
                </MDBRow>
                <MDBInput className="mb-5" id="land" label="Land" />
                <MDBInput
                  className="mb-4"
                  type="email"
                  autocomplete="off"
                  id="email"
                  label="E-Mail Adresse"
                />
                <MDBInput className="mb-5" id="telefon" label="Telefonnr." />

                <hr className="mb-5" />
                <MDBInput
                  className="mb-4"
                  id="mitarbeiter"
                  label="Name des Mitarbeiters"
                />
                <MDBInput
                  className="mb-4"
                  id="passwort"
                  type="password"
                  label="Passwort"
                />
                <MDBBtn type="submit" className="mb-4" block>
                  Registrieren
                </MDBBtn>
                <div className="text-center">
                  <p>
                    Unternehmen ist bereits im System?{" "}
                    <a
                      href="javascript:;"
                      onClick={() => handleFillClick("register_employee")}
                    >
                      <br />
                      Mitarbeiter registrieren
                    </a>
                  </p>
                </div>
