import React, { useState, useEffect } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBBtn,
} from "mdb-react-ui-kit";
import useAxiosPrivate from "../../hooks/useAxiosPrivate";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import axios from "axios";



function PartnerForm(props) {
  const [eori_nr, setEoriNr] = useState("");
  const [companies, setCompanies] = useState([]);
  const [requestedCompany, setrequestedCompany] = useState("")
  const axiosPrivate = useAxiosPrivate();
  const [testRerender, setTestRerender] = useState(false);
  const [notFound, setNotFound] = useState(false);
  const [isCurrentPartnership, setIsCurrentPartnership] = useState(false);
  const [partnerships, setPartnerships] = useState()



  //useffect Abfragen der Firmemliste

  useEffect(() => {
    axiosPrivate.get("partnership/").then((res) => {
      console.log(res.data);
      setPartnerships(res.data);
    });

  }, [testRerender])

  //Debugging 
  useEffect(() => {
    console.log(companies)

  }, [companies]) 

  async function handleEORISearch () {
    //Leeren wenn neu gesucht wird
    setCompanies()
    setNotFound(false)
    await axiosPrivate.get(`companies/${eori_nr}/`).then((res) => {
      console.log(res.data);
      setCompanies(res.data);
      if (partnerships.some(partnerships => partnerships.eori_nr == res.data.eori_nr)){
        setIsCurrentPartnership(true)
        console.log("Partnerschaft exisitiert bereits")
      }
    }).catch((err) => {
      console.log(err)
      setNotFound(true)
    });
  }


  function handleSubmit(e) {
    e.preventDefault();
    //firma hinzufÃ¼gen
    console.log('added partner: ' + companies.eori_nr)
    axiosPrivate
      .post("/partnership/", { added_partner: companies.eori_nr })
      .then(
        alert('Partner angefragt'),
        setTestRerender(true),
        )
      .catch((error) => {
        console.error("There was an error!", error);
      })

    //firma als partner hinterlegen
  }

  return (
    <>
        <MDBContainer>
{/*           <Autocomplete
                  className="mt-1"
                  disablePortal
                  id="combo-box-companies"
                  options={companies}
                  //choose name as label for ui
                  getOptionLabel={(option) => option.name}
                  //when changed, save choosen Partner
                  onChange={(event, value) => {
                    setrequestedCompany(value.eori_nr);
                  }}
                  sx={{ width: 320 }}
                  renderInput={(params) => (
                    <TextField {...params} label="EmpfÃ¤nger der Partnerschaftsanfrage" />
                  )}
                /> */}

          <MDBInput
            className="my-3"
            name="eori_nr"
            label="EORI-Nr."
            type="number"
            onChange={(e) => setEoriNr(e.target.value)}
          />  

            <MDBBtn 
              className="mt-3"
              type="search"
              color="info"
              onClick={(e) => handleEORISearch()}
            >
              Firmen suchen
            </MDBBtn>
          {notFound && <p> EORI-NR nicht gefunden, bitte erneut probieren</p> }
          { (companies?.name != undefined) && <p> Treffer: {companies?.name}</p> 
          }


          <div className="d-grid mx-auto">
            <MDBBtn className="mt-3" type="submit" onClick={(e) => handleSubmit(e)} color="success">
              Anfrage stellen
            </MDBBtn>
          </div>
        </MDBContainer>
    </>
  );
}

export default PartnerForm;
