import { Outlet } from "react-router-dom";
import { useState, useEffect } from "react";
import useRefreshToken from "../hooks/useRefreshToken";
import useAuth from "../hooks/useAuth";
import { MDBSpinner } from "mdb-react-ui-kit";
import useAxiosPrivate from "../hooks/useAxiosPrivate";

const PersistLogin = () => {
    const [isLoading, setIsLoading] = useState(true);
    const refresh = useRefreshToken();
    const { auth, setAuth} = useAuth(); 
    const axiosInstance = useAxiosPrivate();


    useEffect(() => {
        let isMounted = true; 

        const verifyRefreshToken = async () => {
            console.log("Verify Refresh token called")
            try {
                await refresh(); 
                const userData = await axiosInstance.get('user').then((res) => {
                    console.log(res.data);
                });
                setAuth(prev => {
                    console.log(`New UserData : ${userData.data}`);
                    return {
                        ...prev,
                        user: userData.data.username,      
                        company: userData.data.employee.company.name
                    }
                });
            }        
            catch (err) {
                console.error(err);
            }
            finally{
                isMounted && setIsLoading(false);
            }
        }
        const getUserData = async () => {
            console.log("Updata data")
            try {
                await axiosInstance.get('user').then((res) => {
                    console.log(res.data);

                  });
            }
            catch (err) {
                console.error(err);
            }

        }

        // Avoids unwanted call to verifyRefreshToken
        //Verify Refresh Token funktionen nur aufrufen, wenn auth state nicht vorhanden ist
        !auth?.accessToken ?  verifyRefreshToken() : setIsLoading(false);

        return () => isMounted = false;
    }, [])

    //Nur für Debugging und die Anzeige der aktuellen Status: 

    useEffect(() => {
        console.log(`isLoading: ${isLoading}`)
        console.log(`aT: ${JSON.stringify(auth?.accessToken)}`)
    }, [isLoading])

    return (
        <>
            {isLoading
                    ?   <MDBSpinner grow>
                            <span className='visually-hidden'>Loading...</span>
                        </MDBSpinner>
                    : <Outlet />
            }
        </>
    )

}

export default PersistLogin
