import React, { useState } from "react";
import { uploadDokumentHash, getDokumentenhash } from "../../Web3Client";
import Box from "@mui/material/Box";
import Stepper from "@mui/material/Stepper";
import Step from "@mui/material/Step";
import StepLabel from "@mui/material/StepLabel";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";

import { MDBInput, MDBFile, MDBBtn } from "mdb-react-ui-kit";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardSubTitle,
  MDBCardText,
  MDBCardLink,
} from "mdb-react-ui-kit";
import useAxiosPrivate from "../../hooks/useAxiosPrivate"; 
import { useParams } from "react-router-dom";
import axios from "axios";


const steps = ["Dokument beschreiben", "Dokument hochladen", "Bestätigen"];

function OtherDocumentsForm() {

  const axiosInstance = useAxiosPrivate();

  const [type, setType] = React.useState("");
  const [description, setDescription] = React.useState("");
  /*   const [fileID, setFileID] = React.useState();
   */ const [issueDate, setIssueDate] = React.useState("");
  const [fileID, setFileID] = React.useState();
  let filelink = "testlink";

  const [activeStep, setActiveStep] = React.useState(0);
  const [skipped, setSkipped] = React.useState(new Set());

  const [selectedFile, setSelectedFile] = React.useState(null);
  const { transactionId } = useParams();
  const [newDocumentData, setNewDocumentData] = useState();


  const docformData = new FormData();


  // Funktion für Datei Upload
  function handleUpload(e) {
    e.preventDefault();
    const fileformData = new FormData();
    fileformData.append("file", selectedFile);
    console.log(type);
    console.log(description);
    axiosInstance
      .post("/file/", fileformData)
      .then((res) => {
        alert("File Upload success");
        console.log(res.data.id);
        setFileID(res.data.id);
        console.log(fileID)
        filelink = res.data.file;
        console.log(filelink);
      })
      .catch((err) => console.log(err.response));
  }
  async function uDokumentHash(_transaktionID, _file){
    var crypto = require('crypto');
    const fileReader = new FileReader();
    var hashwert = "";
    fileReader.readAsText(_file);
    fileReader.onload = function(evt) {
      hashwert = crypto.createHash('sha512').update(evt.target.result).digest('hex');
    };
    uploadDokumentHash(_transaktionID, hashwert)
    .then((tx) => {
      console.log("Dokument wurde hochgeladen. DokumentenID: " + tx + "hash: "+ hashwert);
      axios.patch("/documents/" + newDocumentData.id + "/", {"blockchain_id": tx})
      return tx;
    })
    .catch((err) => {
      console.log("Hashgenerierung fehlgeschlagen. Fehlermeldung: " + err);
  });
}

function proofFilehash(_DokumentID, _file){
  var crypto = require('crypto');
  var hashwert ="";
  const fileReader = new FileReader();
  fileReader.readAsText(_file);
  fileReader.onload = function(evt) {
    hashwert = crypto.createHash('sha512').update(evt.target.result).digest('hex');
  };
  getDokumentenhash(_DokumentID)
  .then((tx) => {
    if(hashwert===tx){
      return true;
    }else{
      return false;
    }
  })
  .catch((err) => {
    console.log("Fehlgeschlagen. Fehlermeldung: " + err);
});
}

  // Funktion zum Abschicken des Formulars
  async function handleSubmit(e) {
    //Standardverhalten abstellen
    e.preventDefault();
    docformData.append("description", description);
    docformData.append("type", type);
    docformData.append("file", fileID);
    docformData.append("file", fileID);
    docformData.append("issue_date", "2022-03-15");
    docformData.append("transaction", transactionId);
    const tx = await uDokumentHash(transactionId, selectedFile)
    console.log(tx)
    docformData.append("blockchain_id", tx);
    axiosInstance
    .post("/documents/", docformData)
    .then((res) => {
      alert("Doc upload success");
      console.log(res.data);
      setNewDocumentData(res.data)

    })
    .catch((err) => console.log(err.response));
    console.log(selectedFile);

  }

  // Alle Funktionen unterhalb: Steuerung des Steppers

  // Kein Step ist optional
  const isStepOptional = (step) => {
    return;
  };

  const isStepSkipped = (step) => {
    return skipped.has(step);
  };

  const handleNext = () => {
    let newSkipped = skipped;
    if (isStepSkipped(activeStep)) {
      newSkipped = new Set(newSkipped.values());
      newSkipped.delete(activeStep);
    }

    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    setSkipped(newSkipped);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleSkip = () => {
    if (!isStepOptional(activeStep)) {
      // You probably want to guard against something like this,
      // it should never occur unless someone's actively trying to break something.
      throw new Error("You can't skip a step that isn't optional.");
    }

    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    setSkipped((prevSkipped) => {
      const newSkipped = new Set(prevSkipped.values());
      newSkipped.add(activeStep);
      return newSkipped;
    });
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  return (
    <Box sx={{ width: "100%" }}>
      <Stepper activeStep={activeStep}>
        {steps.map((label, index) => {
          const stepProps = {};
          const labelProps = {};
          if (isStepOptional(index)) {
            labelProps.optional = (
              <Typography variant="caption">Optional</Typography>
            );
          }
          if (isStepSkipped(index)) {
            stepProps.completed = false;
          }
          return (
            <Step key={label} {...stepProps}>
              <StepLabel {...labelProps}>{label}</StepLabel>
            </Step>
          );
        })}
      </Stepper>
      {activeStep === steps.length ? (
        <React.Fragment>
          <Typography sx={{ mt: 2, mb: 1 }}>
            All steps completed - you&apos;re finished
          </Typography>
          <Box sx={{ display: "flex", flexDirection: "row", pt: 2 }}>
            <Box sx={{ flex: "1 1 auto" }} />
            <Button onClick={handleReset}>Zurücksetzen</Button>
          </Box>
        </React.Fragment>
      ) : (
        <React.Fragment>
          {activeStep === 0 ? (
            <>
              <MDBInput
                className="my-4"
                name="type"
                id="documentType"
                label="Dokumententyp"
                onChange={(e) => setType(e.target.value)}
                type="text"
              />
              <MDBInput
                wrapperClass="mb-4"
                textarea
                name="description"
                id="documentDescription"
                rows={3}
                onChange={(e) => setDescription(e.target.value)}
                label="Beschreibung (optional)"
              />
            </>
          ) : activeStep === 1 ? (
            <form onSubmit={handleUpload}>
              <MDBFile
                className="my-4"
                label="Dokument auswählen"
                id="customFile"
                onChange={(e) => setSelectedFile(e.target.files[0])}
              />

              <MDBBtn type="submit" block>
                Hochladen
              </MDBBtn>
            </form>
          ) : (
            <>
              <h4 className="mt-4">Daten bestätigen:</h4>
              <MDBCard>
                <MDBCardBody>
                  <MDBCardTitle>
                    Hochgeladene Dokumentenart: {type}
                  </MDBCardTitle>
                  <MDBCardText>{description}</MDBCardText>
                  <MDBCardLink href={filelink}>Link zur Datei</MDBCardLink>
                </MDBCardBody>
              </MDBCard>
            </>
          )}
          <Box sx={{ display: "flex", flexDirection: "row", pt: 2 }}>
            <Button
              color="inherit"
              disabled={activeStep === 0}
              onClick={handleBack}
              sx={{ mr: 1 }}
            >
              Zurück
            </Button>
            <Box sx={{ flex: "1 1 auto" }} />
            {isStepOptional(activeStep) && (
              <Button color="inherit" onClick={handleSkip} sx={{ mr: 1 }}>
                Überspringen
              </Button>
            )}

            {activeStep === steps.length - 1 ? (
              <Button onClick={handleSubmit}>Abschicken</Button>
            ) : (
              <Button onClick={handleNext}>Weiter</Button>
            )}
          </Box>
        </React.Fragment>
      )}
    </Box>
  );
}

export default OtherDocumentsForm;
