import React, { useState, useEffect } from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBIcon,
  MDBSpinner
} from "mdb-react-ui-kit";
import Transaction from "../../components/Transactions/Transaction";
import { useParams } from "react-router-dom";
import TransactionModal from "../../components/Transactions/TransactionModal";
import Form from "react-bootstrap/Form";

import useAxiosPrivate from "../../hooks/useAxiosPrivate";
import { TextRotateUpRounded } from "@mui/icons-material";

function ZollTransactions() {
  const [transactionsData, setTransactionsData] = useState([]);
  const [reload, setreload] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  const axiosInstance = useAxiosPrivate();

  useEffect(async() => {
    await axiosInstance.get("declaration_auth/?customs_office=1").then((res) => {
      setTransactionsData(res.data.transaction);
    }).catch ((err) => {
      console.log(err)
    });
    setIsLoading(false)
  }, [reload]);

  // Sortieralgorithmen
  function sortDescriptionAsc() {
    const myData = []
      .concat(transactionsData)
      .sort((a, b) => (a.description > b.description ? -1 : 1));
    console.log(myData);
    setTransactionsData(myData);
  }

  function sortDescriptionDesc() {
    const myData = []
      .concat(transactionsData)
      .sort((a, b) => (a.description > b.description ? 1 : -1));
    console.log(myData);
    setTransactionsData(myData);
  }

  function sortDateAddedAsc() {
    const myData = []
      .concat(transactionsData)
      .sort((a, b) => (a.date_added > b.date_added ? 1 : -1));
    console.log(myData);
    setTransactionsData(myData);
  }

  function sortDateAddedDesc() {
    const myData = []
      .concat(transactionsData)
      .sort((a, b) => (a.date_added > b.date_added ? -1 : 1));
    console.log(myData);
    setTransactionsData(myData);
  }

  function sortDateUpdatedAsc() {
    const myData = []
      .concat(transactionsData)
      .sort((a, b) => (a.date_processed > b.date_processed ? 1 : -1));
    console.log(myData);
    setTransactionsData(myData);
  }

  function sortDateUpdatedDesc() {
    const myData = []
      .concat(transactionsData)
      .sort((a, b) => (a.date_processed > b.date_processed ? -1 : 1));
    console.log(myData);
    setTransactionsData(myData);
  }

  function sortTransactions(event) {
    if (event.target.value === "DescriptionAsc") {
      sortDescriptionAsc();
      return;
    }
    if (event.target.value === "DescriptionDesc") {
      sortDescriptionDesc();
      return;
    }
    if (event.target.value === "UpdatedAsc") {
      sortDateUpdatedAsc();
      return;
    }
    if (event.target.value === "UpdatedDesc") {
      sortDateUpdatedDesc();
      return;
    }
    if (event.target.value === "AddedAsc") {
      sortDateAddedAsc();
      return;
    }
    if (event.target.value === "AddedDesc") {
      sortDateAddedDesc();
      return;
    }
  }

  return (
    <>
    {isLoading ? (
      <MDBSpinner grow>
        <span className="visually-hidden">LÃ¤dt..</span>
      </MDBSpinner>
    ) : (
    <div>
      <MDBContainer>
        <h3>Zollanmeldungen</h3>

        <nav className="my-3 navbar navbar-expand-lg navbar-light bg-light">
          <MDBContainer>
            <Form.Select
              style={{
                minWidth: 250,
                maxWidth: 250,
              }}
              onChange={(event) => sortTransactions(event)}
            >
              <option value={"UpdatedDesc"}>Zuletzt geÃ¤ndert ðŸ —</option>
              <option value={"UpdatedAsc"}>
                Zuletzt geÃ¤ndert aufsteigend ðŸ •
              </option>
              <option value={"DescriptionDesc"}>
                Beschreibung absteigend ðŸ —
              </option>
              <option value={"DescriptionAsc"}>
                Beschreibung aufsteigend ðŸ •
              </option>
              <option value={"AddedDesc"}>HinzugefÃ¼gt am absteigend ðŸ —</option>
              <option value={"AddedAsc"}>HinzugefÃ¼gt am aufsteigend ðŸ •</option>
            </Form.Select>{" "}
            <div className="float-left">{transactionsData.length} AuftrÃ¤ge</div>
            <TransactionModal setreload={setreload} reload={reload} />
          </MDBContainer>
        </nav>
        {transactionsData.map((transactionData) => (
          <React.Fragment>
            <Transaction
              key={transactionData.id}
              id={transactionData.id}
              title={transactionData.description}
              added={transactionData.date_added}
              exportAddress={transactionData.partnership.partner.name}
              exportStreet={transactionData.partnership.partner.street}
              exportCity={transactionData.partnership.partner.city}
              exportCountry={transactionData.partnership.partner.country_code}
              importAddress={transactionData.importAddress}
              importStreet={transactionData.importStreet}
              importCity={transactionData.importCity}
              importCountry={transactionData.importCountry}
              stakeholderCount={transactionData.stakeholderCount}
              lastChange={transactionData.date_processed}
            />
            <br />
          </React.Fragment>
        ))}
      </MDBContainer>
    </div>
    )
    };
    </>
  );
}

export default ZollTransactions;
